import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  IconButton,
  Snackbar,
  Alert
} from '@mui/material';
import {
  Restaurant,
  CalendarToday,
  LocalHospital,
  Share,
  Visibility,
  Delete,
  ExpandMore,
  Close
} from '@mui/icons-material';
import { StorageService } from '../utils/storage';
import { colors } from '../theme/colors';

const PlansPage = () => {
  const [plans, setPlans] = useState([]);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [viewDialog, setViewDialog] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '' });

  // Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð·Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ð¹ Ð´Ð»Ñ Ð¾Ñ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ñ
  const diseasesData = [
    { value: 'gastritis', label: 'ðŸ¥£ Ð“Ð°ÑÑ‚Ñ€Ð¸Ñ‚', goal: 'ÑÐ½Ð¸Ð·Ð¸Ñ‚ÑŒ Ñ€Ð°Ð·Ð´Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ ÑÐ»Ð¸Ð·Ð¸ÑÑ‚Ð¾Ð¹', bzu: { protein: 90, fat: 70, carbs: 350 }, calories: 2400, vitamins: 'A, E, B2, B6' },
    { value: 'diabetes', label: 'ðŸŽ Ð”Ð¸Ð°Ð±ÐµÑ‚', goal: 'ÑÑ‚Ð°Ð±Ð¸Ð»Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ ÑƒÑ€Ð¾Ð²ÐµÐ½ÑŒ Ð³Ð»ÑŽÐºÐ¾Ð·Ñ‹', bzu: { protein: 100, fat: 80, carbs: 200 }, calories: 2000, vitamins: 'Ñ…Ñ€Ð¾Ð¼, Ð¼Ð°Ð³Ð½Ð¸Ð¹, Ð²Ð¸Ñ‚Ð°Ð¼Ð¸Ð½ D' },
    { value: 'obesity', label: 'âš–ï¸ ÐžÐ¶Ð¸Ñ€ÐµÐ½Ð¸Ðµ', goal: 'ÑÐ½Ð¸Ð¶ÐµÐ½Ð¸Ðµ ÐºÐ°Ð»Ð¾Ñ€Ð¸Ð¹Ð½Ð¾ÑÑ‚Ð¸', bzu: { protein: 120, fat: 60, carbs: 150 }, calories: 1700, vitamins: 'D, C, Ñ†Ð¸Ð½Ðº, L-ÐºÐ°Ñ€Ð½Ð¸Ñ‚Ð¸Ð½' },
    { value: 'anemia', label: 'ðŸ©¸ ÐÐ½ÐµÐ¼Ð¸Ñ', goal: 'Ð¿Ð¾Ð²Ñ‹ÑÐ¸Ñ‚ÑŒ ÑƒÑ€Ð¾Ð²ÐµÐ½ÑŒ Ð¶ÐµÐ»ÐµÐ·Ð°', bzu: { protein: 100, fat: 80, carbs: 300 }, calories: 2300, vitamins: 'Ð¶ÐµÐ»ÐµÐ·Ð¾, Ð²Ð¸Ñ‚Ð°Ð¼Ð¸Ð½ C, Ñ„Ð¾Ð»Ð¸ÐµÐ²Ð°Ñ ÐºÐ¸ÑÐ»Ð¾Ñ‚Ð°' },
    { value: 'hypertension', label: 'â¤ï¸ Ð“Ð¸Ð¿ÐµÑ€Ñ‚Ð¾Ð½Ð¸Ñ', goal: 'ÑÐ½Ð¸Ð·Ð¸Ñ‚ÑŒ Ð´Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ', bzu: { protein: 90, fat: 65, carbs: 300 }, calories: 2200, vitamins: 'Ð¼Ð°Ð³Ð½Ð¸Ð¹, ÐºÐ°Ð»Ð¸Ð¹, ÐºÐ¾ÑÐ½Ð·Ð¸Ð¼ Q10' }
  ];

  useEffect(() => {
    // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ
    StorageService.initializeDefaultData().then(() => {
      loadPlans();
    });
  }, []);

  const loadPlans = async () => {
    try {
      const data = await StorageService.getPlans();
      console.log("Ð—Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð½Ñ‹Ðµ Ð¿Ð»Ð°Ð½Ñ‹ Ð¸Ð· localStorage:", data);
      setPlans(data);
    } catch (error) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸:", error);
    }
  };

  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ Ð¿Ð»Ð°Ð½Ð°
  const deletePlanFromServer = async (planId) => {
    try {
      await StorageService.deletePlan(planId);
      setPlans((prev) => prev.filter((p) => p.id !== planId));
      console.log("ÐŸÐ»Ð°Ð½ ÑƒÐ´Ð°Ð»ÐµÐ½:", planId);
    } catch (error) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ:", error);
    }
  };

  const viewPlan = (plan) => {
    setSelectedPlan(plan);
    setViewDialog(true);
  };

  const deletePlan = (planId) => {
    deletePlanFromServer(planId);
  };

  const sharePlan = (plan) => {
    navigator.clipboard.writeText(`ÐœÐ¾Ð¹ Ð¿Ð»Ð°Ð½ Ð¿Ð¸Ñ‚Ð°Ð½Ð¸Ñ: ${plan.name}`);
    setSnackbar({ open: true, message: 'âœ… Ð¡ÑÑ‹Ð»ÐºÐ° Ð½Ð° Ð¿Ð»Ð°Ð½ ÑÐºÐ¾Ð¿Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð° Ð² Ð±ÑƒÑ„ÐµÑ€ Ð¾Ð±Ð¼ÐµÐ½Ð°!' });
  };

  const getDiseaseColor = (disease) => {
    const diseaseColors = {
      'gastritis': 'success',
      'diabetes': 'error',
      'obesity': 'warning',
      'anemia': 'secondary',
      'hypertension': 'info'
    };
    return diseaseColors[disease] || 'default';
  };

const getDiseaseLabel = (diseaseValue) => {
  if (!diseaseValue) return 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾';

  // Ð•ÑÐ»Ð¸ disease â€” Ð¾Ð±ÑŠÐµÐºÑ‚ (Ð¸Ð· Ð‘Ð”), Ð²Ð·ÑÑ‚ÑŒ Ð¿Ð¾Ð»Ðµ name
  if (typeof diseaseValue === 'object' && diseaseValue.name) {
    return diseaseValue.name;
  }

  // Ð˜Ð½Ð°Ñ‡Ðµ â€” ÑÑ‚Ð¾ ÑÑ‚Ñ€Ð¾ÐºÐ° (â€˜diabetesâ€™ Ð¸ Ñ‚.Ð¿.)
  const disease = diseasesData.find(d => d.value === diseaseValue);
  return disease ? disease.label : diseaseValue;
};


  const getDiseaseInfo = (diseaseValue) => {
    return diseasesData.find(d => d.value === diseaseValue) || {};
  };

  return (
    <Container maxWidth="lg" style={{ padding: '32px 0', minHeight: '80vh', position: 'relative' }}>
      <Box style={{ marginBottom: '32px', textAlign: 'center' }}>
        <Typography variant="h4" component="h1" gutterBottom style={{ fontWeight: 'bold', color: colors.text.primary }}>
          ðŸ“‹ ÐœÐ¾Ð¸ Ð¿Ð»Ð°Ð½Ñ‹ Ð¿Ð¸Ñ‚Ð°Ð½Ð¸Ñ
        </Typography>
        <Typography variant="body1" style={{ color: colors.text.secondary }}>
          Ð’ÑÐµ ÑÐ¾Ð·Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð²Ð°Ð¼Ð¸ Ð¸Ð½Ð´Ð¸Ð²Ð¸Ð´ÑƒÐ°Ð»ÑŒÐ½Ñ‹Ðµ Ñ€Ð°Ñ†Ð¸Ð¾Ð½Ñ‹ Ð¿Ð¸Ñ‚Ð°Ð½Ð¸Ñ
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {plans.map((plan, index) => {
          const diseaseInfo = getDiseaseInfo(plan.disease) || {};
          const bzu = plan.bzu || diseaseInfo.bzu || { protein: 0, fat: 0, carbs: 0 };
          const calories = plan.totalCalories || diseaseInfo.calories || 0;

          return (
            <Grid 
              item 
              xs={12} 
              md={6} 
              key={plan.id}
              style={{
                animation: `fadeInUp 0.6s ease-out ${index * 0.1}s both`
              }}
            >
              <Card style={{
                height: '100%', 
                display: 'flex', 
                flexDirection: 'column',
                background: 'rgba(255,255,255,0.85)',
                backdropFilter: 'blur(20px) saturate(180%)',
                WebkitBackdropFilter: 'blur(20px) saturate(180%)',
                border: '1px solid rgba(255,255,255,0.4)',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                cursor: 'pointer'
              }}
              sx={{
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: '0 12px 30px rgba(0,0,0,0.15)'
                }
              }}>
                <CardContent style={{ flexGrow: 1 }}>
                  <Box style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: '16px' }}>
                    <Typography variant="h6" component="h2" gutterBottom style={{ fontWeight: 'bold', color: '#2D3748' }}>
                      {plan.name}
                    </Typography>
                    <Chip 
                      label={getDiseaseLabel(plan.disease)}
                      color={getDiseaseColor(plan.disease)}
                      size="small"
                    />
                  </Box>

                  <Box style={{ marginBottom: '16px' }}>
                    <Box style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
                      <CalendarToday style={{ fontSize: 16, marginRight: '8px', color: '#718096' }} />
                      <Typography variant="body2" style={{ color: '#718096' }}>
                        ÐŸÐµÑ€Ð¸Ð¾Ð´: {plan.duration === 'week' ? 'ÐÐµÐ´ÐµÐ»Ñ' : 
                                plan.duration === 'month' ? 'ÐœÐµÑÑÑ†' :
                                plan.duration === 'half-year' ? 'ÐŸÐ¾Ð»Ð³Ð¾Ð´Ð°' : 'Ð“Ð¾Ð´'}
                      </Typography>
                    </Box>
                    <Box style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
                      <Restaurant style={{ fontSize: 16, marginRight: '8px', color: '#718096' }} />
                      <Typography variant="body2" style={{ color: '#718096' }}>
                        {calories} ÐºÐºÐ°Ð»/Ð´ÐµÐ½ÑŒ
                      </Typography>
                    </Box>
                    <Box style={{ display: 'flex', alignItems: 'center' }}>
                      <LocalHospital style={{ fontSize: 16, marginRight: '8px', color: '#718096' }} />
                      <Typography variant="body2" style={{ color: '#718096' }}>
                        Ð¡Ð¾Ð·Ð´Ð°Ð½: {new Date(plan.createdAt).toLocaleDateString()}
                      </Typography>
                    </Box>
                  </Box>

                  <Box style={{ display: 'flex', gap: '8px', marginBottom: '16px', flexWrap: 'wrap' }}>
                    <Chip 
                      label={`Ð‘: ${bzu.protein}g`} 
                      size="small" 
                      variant="outlined" 
                      style={{ borderColor: '#667eea', color: '#667eea' }}
                    />
                    <Chip 
                      label={`Ð–: ${bzu.fat}g`} 
                      size="small" 
                      variant="outlined" 
                      style={{ borderColor: '#764ba2', color: '#764ba2' }}
                    />
                    <Chip 
                      label={`Ð£: ${bzu.carbs}g`} 
                      size="small" 
                      variant="outlined" 
                      style={{ borderColor: '#f093fb', color: '#f093fb' }}
                    />
                  </Box>

                  <Typography variant="body2" style={{ color: '#4A5568', fontStyle: 'italic' }}>
                    {plan.recommendations || diseaseInfo.goal || 'Ð˜Ð½Ð´Ð¸Ð²Ð¸Ð´ÑƒÐ°Ð»ÑŒÐ½Ñ‹Ðµ Ñ€ÐµÐºÐ¾Ð¼ÐµÐ½Ð´Ð°Ñ†Ð¸Ð¸ Ð¿Ð¾ Ð¿Ð¸Ñ‚Ð°Ð½Ð¸ÑŽ'}
                  </Typography>
                </CardContent>

                <CardActions style={{ justifyContent: 'space-between', padding: '16px', borderTop: '1px solid #E2E8F0' }}>
                  <Button 
                    variant="outlined" 
                    size="small" 
                    startIcon={<Visibility />}
                    onClick={() => viewPlan(plan)}
                    style={{ borderColor: '#667eea', color: '#667eea' }}
                  >
                    ÐŸÑ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€ÐµÑ‚ÑŒ
                  </Button>
                  <Box style={{ display: 'flex', gap: '8px' }}>
                    <Button 
                      startIcon={<Share />} 
                      size="small"
                      onClick={() => sharePlan(plan)}
                      style={{ color: '#718096' }}
                    >
                      ÐŸÐ¾Ð´ÐµÐ»Ð¸Ñ‚ÑŒÑÑ
                    </Button>
                    <Button 
                      startIcon={<Delete />} 
                      size="small"
                      onClick={() => deletePlan(plan.id)}
                      style={{ color: '#E53E3E' }}
                    >
                      Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ
                    </Button>
                  </Box>
                </CardActions>
              </Card>
            </Grid>
          );
        })}
      </Grid>

      {plans.length === 0 && (
        <Box textAlign="center" style={{ padding: '80px 0' }}>
          <Restaurant style={{ fontSize: 96, color: '#A0AEC0', marginBottom: '24px', opacity: 0.5 }} />
          <Typography variant="h5" gutterBottom style={{ color: '#718096', fontWeight: 'bold' }}>
            Ð£ Ð²Ð°Ñ Ð¿Ð¾ÐºÐ° Ð½ÐµÑ‚ ÑÐ¾Ð·Ð´Ð°Ð½Ð½Ñ‹Ñ… Ð¿Ð»Ð°Ð½Ð¾Ð²
          </Typography>
          <Typography variant="body1" style={{ color: '#A0AEC0', marginBottom: '32px' }}>
            Ð¡Ð¾Ð·Ð´Ð°Ð¹Ñ‚Ðµ ÑÐ²Ð¾Ð¹ Ð¿ÐµÑ€Ð²Ñ‹Ð¹ Ð¸Ð½Ð´Ð¸Ð²Ð¸Ð´ÑƒÐ°Ð»ÑŒÐ½Ñ‹Ð¹ Ñ€Ð°Ñ†Ð¸Ð¾Ð½ Ð¿Ð¸Ñ‚Ð°Ð½Ð¸Ñ
          </Typography>
          <Button 
            variant="contained" 
            href="/generator"
            style={{ 
              background: 'linear-gradient(45deg, #667eea 0%, #764ba2 100%)',
              padding: '12px 32px',
              fontSize: '1.1rem',
              borderRadius: '25px'
            }}
          >
            Ð¡Ð¾Ð·Ð´Ð°Ñ‚ÑŒ Ñ€Ð°Ñ†Ð¸Ð¾Ð½
          </Button>
        </Box>
      )}

      {/* Ð”Ð¸Ð°Ð»Ð¾Ð³ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€Ð° Ð¿Ð»Ð°Ð½Ð° */}
      <Dialog 
        open={viewDialog} 
        onClose={() => setViewDialog(false)} 
        maxWidth="lg" 
        fullWidth
        PaperProps={{
          style: {
            borderRadius: '28px',
            padding: '0',
            overflow: 'hidden',
            boxShadow: '0 25px 50px rgba(0,0,0,0.25)',
            background: 'linear-gradient(to bottom, #FAFAFA 0%, #FFFFFF 100%)'
          }
        }}
      >
        <DialogTitle style={{ 
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)',
          color: 'white',
          padding: '32px 40px',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          position: 'relative',
          overflow: 'hidden'
        }}>
          {/* Ð”ÐµÐºÐ¾Ñ€Ð°Ñ‚Ð¸Ð²Ð½Ñ‹Ðµ ÑÐ»ÐµÐ¼ÐµÐ½Ñ‚Ñ‹ */}
          <Box style={{
            position: 'absolute',
            top: '-50px',
            right: '-50px',
            width: '200px',
            height: '200px',
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.15)',
            filter: 'blur(40px)'
          }} />
          <Box style={{
            position: 'absolute',
            bottom: '-30px',
            left: '-30px',
            width: '150px',
            height: '150px',
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.1)',
            filter: 'blur(30px)'
          }} />

          <Box style={{ display: 'flex', alignItems: 'center', gap: '16px', position: 'relative', zIndex: 1 }}>
            <Box style={{
              background: 'rgba(255,255,255,0.25)',
              borderRadius: '16px',
              padding: '12px',
              backdropFilter: 'blur(10px)',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
            }}>
              <Typography variant="h4" style={{ margin: 0, fontSize: '2rem' }}>ðŸ½ï¸</Typography>
            </Box>
            <Box>
              <Typography variant="h4" style={{ fontWeight: 800, letterSpacing: '-0.02em', marginBottom: '4px' }}>
                {selectedPlan?.name}
              </Typography>
              <Typography variant="body1" style={{ opacity: 0.95, fontWeight: 500 }}>
                Ð”ÐµÑ‚Ð°Ð»ÑŒÐ½Ñ‹Ð¹ Ð¿Ñ€Ð¾ÑÐ¼Ð¾Ñ‚Ñ€ Ñ€Ð°Ñ†Ð¸Ð¾Ð½Ð° Ð¿Ð¸Ñ‚Ð°Ð½Ð¸Ñ
              </Typography>
            </Box>
          </Box>
          <IconButton 
            onClick={() => setViewDialog(false)} 
            style={{ 
              color: 'white',
              background: 'rgba(255,255,255,0.2)',
