const diseasePlans = {
  gastritis: {
    goal: 'ÑÐ½Ð¸Ð·Ð¸Ñ‚ÑŒ Ñ€Ð°Ð·Ð´Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ ÑÐ»Ð¸Ð·Ð¸ÑÑ‚Ð¾Ð¹, Ð½Ð¾Ñ€Ð¼Ð°Ð»Ð¸Ð·Ð¾Ð²Ð°Ñ‚ÑŒ ÑÐµÐºÑ€ÐµÑ†Ð¸ÑŽ',
