      breakfast: { name: 'ÐžÐ²ÑÑÐ½Ð°Ñ ÐºÐ°ÑˆÐ° Ð½Ð° Ð²Ð¾Ð´Ðµ Ñ Ð¼Ð¾Ð»Ð¾ÐºÐ¾Ð¼', description: 'ÑÐ¹Ñ†Ð¾ Ð²ÑÐ¼ÑÑ‚ÐºÑƒ, ÑÐ»Ð°Ð±Ñ‹Ð¹ Ñ‡Ð°Ð¹', calories: 400, protein: 18, fat: 12, carbs: 60 },
      snack1: { name: 'Ð‘Ð°Ð½Ð°Ð½', description: 'Ñ‚Ñ‘Ð¿Ð»Ð¾Ðµ Ð¼Ð¾Ð»Ð¾ÐºÐ¾', calories: 200, protein: 8, fat: 5, carbs: 30 },
      lunch: { name: 'Ð¡ÑƒÐ¿-Ð¿ÑŽÑ€Ðµ Ð¸Ð· ÐºÐ°Ñ€Ñ‚Ð¾Ñ„ÐµÐ»Ñ Ð¸ Ð¼Ð¾Ñ€ÐºÐ¾Ð²Ð¸', description: 'Ð¿Ð°Ñ€Ð¾Ð²Ñ‹Ðµ ÐºÐ¾Ñ‚Ð»ÐµÑ‚Ñ‹, Ñ€Ð¸Ñ, ÐºÐ¾Ð¼Ð¿Ð¾Ñ‚', calories: 600, protein: 25, fat: 20, carbs: 80 },
      dinner: { name: 'ÐœÐ°Ð½Ð½Ð°Ñ ÐºÐ°ÑˆÐ°', description: 'Ñ‚Ð²Ð¾Ñ€Ð¾Ð³ 5%, Ñ€Ð¾Ð¼Ð°ÑˆÐºÐ¾Ð²Ñ‹Ð¹ Ñ‡Ð°Ð¹', calories: 400, protein: 20, fat: 10, carbs: 55 }
    },
    vitamins: 'A, E, B2, B6 (Ð²Ð¾ÑÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÑŽÑ‚ ÑÐ»Ð¸Ð·Ð¸ÑÑ‚ÑƒÑŽ Ð¶ÐµÐ»ÑƒÐ´ÐºÐ°)'
  },
  diabetes: {
    goal: 'ÑÑ‚Ð°Ð±Ð¸Ð»Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ ÑƒÑ€Ð¾Ð²ÐµÐ½ÑŒ Ð³Ð»ÑŽÐºÐ¾Ð·Ñ‹, ÑÐ½Ð¸Ð·Ð¸Ñ‚ÑŒ Ð¸Ð½ÑÑƒÐ»Ð¸Ð½Ð¾Ñ€ÐµÐ·Ð¸ÑÑ‚ÐµÐ½Ñ‚Ð½Ð¾ÑÑ‚ÑŒ',
    bzu: { protein: 100, fat: 80, carbs: 200 },
    calories: 2000,
    meals: {
      breakfast: { name: 'Ð“Ñ€ÐµÑ‡ÐºÐ° Ñ Ð¾Ñ‚Ð²Ð°Ñ€Ð½Ð¾Ð¹ ÐºÑƒÑ€Ð¸Ñ†ÐµÐ¹', description: 'ÐºÐ¾Ñ„Ðµ Ð±ÐµÐ· ÑÐ°Ñ…Ð°Ñ€Ð°', calories: 450, protein: 35, fat: 15, carbs: 45 },
      snack1: { name: 'Ð¯Ð±Ð»Ð¾ÐºÐ¾', description: 'Ð³Ð¾Ñ€ÑÑ‚ÑŒ Ð¾Ñ€ÐµÑ…Ð¾Ð²', calories: 200, protein: 5, fat: 12, carbs: 20 },
      lunch: { name: 'ÐžÐ²Ð¾Ñ‰Ð½Ð¾Ð¹ ÑÑƒÐ¿', description: 'Ñ€Ñ‹Ð±Ð° Ð½Ð° Ð¿Ð°Ñ€Ñƒ, Ñ‚ÑƒÑˆÑ‘Ð½Ñ‹Ðµ Ð¾Ð²Ð¾Ñ‰Ð¸', calories: 550, protein: 30, fat: 20, carbs: 50 },
      dinner: { name: 'Ð¢Ð²Ð¾Ñ€Ð¾Ð³ Ñ ÐºÐ¾Ñ€Ð¸Ñ†ÐµÐ¹', description: 'Ð¾Ñ‚Ñ€ÑƒÐ±Ð½Ð¾Ð¹ Ñ…Ð»ÐµÐ±ÐµÑ†', calories: 300, protein: 25, fat: 10, carbs: 25 }
    },
    vitamins: 'Ñ…Ñ€Ð¾Ð¼, Ð¼Ð°Ð³Ð½Ð¸Ð¹, Ð²Ð¸Ñ‚Ð°Ð¼Ð¸Ð½ D, Ð¾Ð¼ÐµÐ³Ð°-3, B1, B6'
  },
  obesity: {
    goal: 'ÑÐ½Ð¸Ð¶ÐµÐ½Ð¸Ðµ ÐºÐ°Ð»Ð¾Ñ€Ð¸Ð¹Ð½Ð¾ÑÑ‚Ð¸ Ð¸ Ð°Ð¿Ð¿ÐµÑ‚Ð¸Ñ‚Ð°, ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ Ð¼Ñ‹ÑˆÑ†',
    bzu: { protein: 120, fat: 60, carbs: 150 },
    calories: 1700,
    meals: {
      breakfast: { name: 'ÐžÐ¼Ð»ÐµÑ‚ Ð¸Ð· 2 ÑÐ¸Ñ† Ñ Ð¾Ð²Ð¾Ñ‰Ð°Ð¼Ð¸', description: 'Ð·ÐµÐ»Ñ‘Ð½Ñ‹Ð¹ Ñ‡Ð°Ð¹', calories: 350, protein: 25, fat: 20, carbs: 15 },
      snack1: { name: 'ÐšÐµÑ„Ð¸Ñ€ 1%', description: 'ÑÐ±Ð»Ð¾ÐºÐ¾', calories: 150, protein: 8, fat: 3, carbs: 20 },
      lunch: { name: 'ÐšÑƒÑ€Ð¸Ð½Ð°Ñ Ð³Ñ€ÑƒÐ´ÐºÐ°', description: 'Ð³Ñ€ÐµÑ‡ÐºÐ°, ÑÐ°Ð»Ð°Ñ‚ Ñ Ð¾Ð»Ð¸Ð²ÐºÐ¾Ð²Ñ‹Ð¼ Ð¼Ð°ÑÐ»Ð¾Ð¼', calories: 500, protein: 45, fat: 15, carbs: 40 },
      dinner: { name: 'Ð—Ð°Ð¿ÐµÑ‡Ñ‘Ð½Ð½Ð°Ñ Ñ€Ñ‹Ð±Ð°', description: 'Ñ‚ÑƒÑˆÑ‘Ð½Ñ‹Ðµ ÐºÐ°Ð±Ð°Ñ‡ÐºÐ¸', calories: 400, protein: 35, fat: 12, carbs: 25 }
    },
    vitamins: 'D, C, Ñ†Ð¸Ð½Ðº, L-ÐºÐ°Ñ€Ð½Ð¸Ñ‚Ð¸Ð½, ÐºÐ¾Ð¼Ð¿Ð»ÐµÐºÑ B-Ð³Ñ€ÑƒÐ¿Ð¿Ñ‹'
  },
  anemia: {
    goal: 'Ð¿Ð¾Ð²Ñ‹ÑÐ¸Ñ‚ÑŒ ÑƒÑ€Ð¾Ð²ÐµÐ½ÑŒ Ð¶ÐµÐ»ÐµÐ·Ð° Ð¸ Ð³ÐµÐ¼Ð¾Ð³Ð»Ð¾Ð±Ð¸Ð½Ð°',
    bzu: { protein: 100, fat: 80, carbs: 300 },
    calories: 2300,
    meals: {
      breakfast: { name: 'ÐžÐ²ÑÑÐ½ÐºÐ° Ñ ÑÐ±Ð»Ð¾ÐºÐ¾Ð¼', description: 'Ñ‡Ð°Ð¹ Ñ ÑˆÐ¸Ð¿Ð¾Ð²Ð½Ð¸ÐºÐ¾Ð¼', calories: 400, protein: 15, fat: 10, carbs: 70 },
      snack1: { name: 'Ð“Ñ€Ð°Ð½Ð°Ñ‚Ð¾Ð²Ñ‹Ð¹ ÑÐ¾Ðº', description: 'Ð¾Ñ€ÐµÑ…Ð¸', calories: 250, protein: 8, fat: 15, carbs: 25 },
      lunch: { name: 'Ð“Ñ€ÐµÑ‡ÐºÐ° Ñ Ð³Ð¾Ð²ÑÐ´Ð¸Ð½Ð¾Ð¹', description: 'ÑÐ°Ð»Ð°Ñ‚ Ð¸Ð· ÑÐ²Ñ‘ÐºÐ»Ñ‹, Ñ‡Ñ‘Ñ€Ð½Ñ‹Ð¹ Ñ…Ð»ÐµÐ±', calories: 600, protein: 40, fat: 25, carbs: 65 },
      dinner: { name: 'Ð—Ð°Ð¿ÐµÐºÐ°Ð½ÐºÐ° Ð¸Ð· Ñ‚Ð²Ð¾Ñ€Ð¾Ð³Ð° Ñ Ð¸Ð·ÑŽÐ¼Ð¾Ð¼', description: '', calories: 350, protein: 25, fat: 12, carbs: 40 }
    },
    vitamins: 'Ð¶ÐµÐ»ÐµÐ·Ð¾, Ð²Ð¸Ñ‚Ð°Ð¼Ð¸Ð½ C, Ñ„Ð¾Ð»Ð¸ÐµÐ²Ð°Ñ ÐºÐ¸ÑÐ»Ð¾Ñ‚Ð°, B12'
  },
  hypertension: {
    goal: 'ÑÐ½Ð¸Ð·Ð¸Ñ‚ÑŒ Ð´Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ, ÑƒÐ»ÑƒÑ‡ÑˆÐ¸Ñ‚ÑŒ ÑÐ¾ÑÑƒÐ´Ð¸ÑÑ‚Ñ‹Ð¹ Ñ‚Ð¾Ð½ÑƒÑ',
    bzu: { protein: 90, fat: 65, carbs: 300 },
    calories: 2200,
    meals: {
      breakfast: { name: 'ÐžÐ²ÑÑÐ½ÐºÐ° Ñ Ð±Ð°Ð½Ð°Ð½Ð¾Ð¼', description: 'Ñ‚Ñ€Ð°Ð²ÑÐ½Ð¾Ð¹ Ñ‡Ð°Ð¹', calories: 400, protein: 15, fat: 10, carbs: 70 },
      snack1: { name: 'Ð™Ð¾Ð³ÑƒÑ€Ñ‚ Ð±ÐµÐ· ÑÐ°Ñ…Ð°Ñ€Ð°', description: '', calories: 150, protein: 8, fat: 5, carbs: 15 },
      lunch: { name: 'Ð¢ÑƒÑˆÑ‘Ð½Ð°Ñ Ñ€Ñ‹Ð±Ð°', description: 'Ð¾Ð²Ð¾Ñ‰Ð½Ð¾Ðµ Ñ€Ð°Ð³Ñƒ, ÐºÐ¾Ð¼Ð¿Ð¾Ñ‚ Ð±ÐµÐ· ÑÐ°Ñ…Ð°Ñ€Ð°', calories: 550, protein: 35, fat: 20, carbs: 50 },
      dinner: { name: 'ÐšÑƒÑ€Ð¸Ð½Ð¾Ðµ Ñ„Ð¸Ð»Ðµ', description: 'ÑÐ°Ð»Ð°Ñ‚ Ð¸Ð· Ð¾Ð³ÑƒÑ€Ñ†Ð¾Ð², Ñ€ÑÐ¶ÐµÐ½ÐºÐ°', calories: 400, protein: 30, fat: 15, carbs: 35 }
    },
    vitamins: 'Ð¼Ð°Ð³Ð½Ð¸Ð¹, ÐºÐ°Ð»Ð¸Ð¹, ÐºÐ¾ÑÐ½Ð·Ð¸Ð¼ Q10, Ð²Ð¸Ñ‚Ð°Ð¼Ð¸Ð½ C, Ð¾Ð¼ÐµÐ³Ð°-3'
  }
};

const GeneratorPage = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: '',
    age: '',
    weight: '',
    height: '',
    gender: '',
    disease: '',
    allergies: '',
    duration: 'week',
    activityLevel: 'moderate'
  });

  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [dietPlan, setDietPlan] = useState(null);
  const [saveSuccess, setSaveSuccess] = useState(false);

  const steps = ['ÐŸÐµÑ€ÑÐ¾Ð½Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ', 'ÐœÐµÐ´Ð¸Ñ†Ð¸Ð½ÑÐºÐ°Ñ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ', 'Ð ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚'];
  const API_URL = "http://localhost:8080/api/plans";

  const calculateCalories = () => {
    const age = parseInt(userData.age) || 30;
    const weight = parseInt(userData.weight) || 70;
    const height = parseInt(userData.height) || 170;
    
    let bmr = userData.gender === 'male' 
      ? 10 * weight + 6.25 * height - 5 * age + 5
      : 10 * weight + 6.25 * height - 5 * age - 161;
    
    const multipliers = {
      sedentary: 1.2,
      light: 1.375,
      moderate: 1.55,
      high: 1.725,
      athlete: 1.9
    };
    
    return Math.round(bmr * (multipliers[userData.activityLevel] || 1.55));
  };

  const generateDietPlan = () => {
    const baseCalories = calculateCalories();
    const diseasePlan = diseasePlans[userData.disease] || diseasePlans.gastritis;
    
    const adjustedCalories = Math.round(baseCalories * (diseasePlan.calories / 2000));
    const days = ['ÐŸÐ¾Ð½ÐµÐ´ÐµÐ»ÑŒÐ½Ð¸Ðº', 'Ð’Ñ‚Ð¾Ñ€Ð½Ð¸Ðº', 'Ð¡Ñ€ÐµÐ´Ð°', 'Ð§ÐµÑ‚Ð²ÐµÑ€Ð³', 'ÐŸÑÑ‚Ð½Ð¸Ñ†Ð°', 'Ð¡ÑƒÐ±Ð±Ð¾Ñ‚Ð°', 'Ð’Ð¾ÑÐºÑ€ÐµÑÐµÐ½ÑŒÐµ'];
    
    return {
      name: `ÐŸÐ»Ð°Ð½ Ð´Ð»Ñ ${userData.name} (${diseases.find(d => d.value === userData.disease)?.label || userData.disease})`,
      duration: userData.duration,
      disease: userData.disease,
      totalCalories: adjustedCalories,
      bzu: diseasePlan.bzu,
      recommendations: `Ð¦ÐµÐ»ÑŒ: ${diseasePlan.goal}`,
      vitamins: diseasePlan.vitamins,
      createdAt: new Date().toISOString(),
      dailyMeals: days.map(day => ({
        day,
        meals: [
          { type: 'Ð—Ð°Ð²Ñ‚Ñ€Ð°Ðº', ...diseasePlan.meals.breakfast },
          { type: 'ÐŸÐµÑ€ÐµÐºÑƒÑ', ...diseasePlan.meals.snack1 },
          { type: 'ÐžÐ±ÐµÐ´', ...diseasePlan.meals.lunch },
          { type: 'Ð£Ð¶Ð¸Ð½', ...diseasePlan.meals.dinner }
        ]
      }))
    };
  };

const savePlanToDatabase = async (plan) => {
  try {
    // ÐŸÑ€ÐµÐ¾Ð±Ñ€Ð°Ð·ÑƒÐµÐ¼ ÑÑ‚Ñ€ÑƒÐºÑ‚ÑƒÑ€Ñƒ Ð´Ð»Ñ Ð±ÑÐºÐµÐ½Ð´Ð°
    const formattedPlan = {
      ...plan,
      // Ð£Ð±Ð¸Ñ€Ð°ÐµÐ¼ ÑÑ‚Ñ€Ð¾ÐºÐ¾Ð²Ð¾Ðµ Ð¿Ð¾Ð»Ðµ disease
      disease: undefined,
      // ÐŸÑ€ÐµÐ¾Ð±Ñ€Ð°Ð·ÑƒÐµÐ¼ diseases Ð² Ð¼Ð°ÑÑÐ¸Ð² Ð¾Ð±ÑŠÐµÐºÑ‚Ð¾Ð²
      diseases: plan.disease 
        ? [{ name: plan.disease }] // Ð•ÑÐ»Ð¸ ÐµÑÑ‚ÑŒ disease (ÑÑ‚Ñ€Ð¾ÐºÐ°), ÑÐ¾Ð·Ð´Ð°ÐµÐ¼ Ð¼Ð°ÑÑÐ¸Ð² Ñ Ð¾Ð±ÑŠÐµÐºÑ‚Ð¾Ð¼
        : (plan.diseases || []).map(d => 
            typeof d === 'string' ? { name: d } : d
          )
    };

    // Ð£Ð´Ð°Ð»ÑÐµÐ¼ undefined Ð¿Ð¾Ð»Ñ
    delete formattedPlan.disease;

    console.log("ÐžÑ‚Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ð¹ Ð¿Ð»Ð°Ð½:", JSON.stringify(formattedPlan, null, 2));
    
    const response = await fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(formattedPlan)
    });

    const responseText = await response.text();

    if (!response.ok) {
      let errorMessage = "ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ð¸ Ð¿Ð»Ð°Ð½Ð°";
      try {
        const errorData = JSON.parse(responseText);
        errorMessage = errorData.message || errorData.error || errorMessage;
      } catch (e) {
        errorMessage = responseText || errorMessage;
      }
      
      throw new Error(`${errorMessage} (Ð¡Ñ‚Ð°Ñ‚ÑƒÑ: ${response.status})`);
    }

    const savedPlan = JSON.parse(responseText);
    console.log("ÐŸÐ»Ð°Ð½ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½:", savedPlan);
    setSaveSuccess(true);
    return savedPlan;
    
  } catch (error) {
    console.error("Ð”ÐµÑ‚Ð°Ð»Ð¸ Ð¾ÑˆÐ¸Ð±ÐºÐ¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ:", error);
    setError(`ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð¿Ð»Ð°Ð½: ${error.message}`);
    return null;
  }
};

  const handleNext = () => {
    if (activeStep === 0) {
      if (!userData.name || !userData.age || !userData.weight || !userData.height || !userData.gender) {
        setError('ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð·Ð°Ð¿Ð¾Ð»Ð½Ð¸Ñ‚Ðµ Ð²ÑÐµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ðµ Ð¿Ð¾Ð»Ñ');
        return;
      }
    }
    if (activeStep === 1 && !userData.disease) {
      setError('ÐŸÐ¾Ð¶Ð°Ð»ÑƒÐ¹ÑÑ‚Ð°, Ð²Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð·Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ðµ');
      return;
    }
    setError('');
    setActiveStep(prev => prev + 1);
    
    if (activeStep === 1) {
      setLoading(true);
      setTimeout(async () => {
        const plan = generateDietPlan();
        setDietPlan(plan);
        
        // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð¿Ð»Ð°Ð½ Ð² Ð±Ð°Ð·Ñƒ Ð´Ð°Ð½Ð½Ñ‹Ñ…
        await savePlanToDatabase(plan);
        
        setLoading(false);
      }, 2000);
    }
  };

  const handleBack = () => {
    setActiveStep(prev => prev - 1);
    setError('');
  };

  const handleViewAllPlans = () => {
    navigate('/plans');
  };

  return (
    <Container maxWidth="lg" style={{ padding: '32px 0' }}>
      <Paper elevation={3} style={{ padding: '32px', marginBottom: '32px' }}>
        <Typography variant="h4" component="h1" gutterBottom align="center" style={{ color: '#2E8B57' }}>
          ðŸ½ï¸ Ð“ÐµÐ½ÐµÑ€Ð°Ñ‚Ð¾Ñ€ Ð¸Ð½Ð´Ð¸Ð²Ð¸Ð´ÑƒÐ°Ð»ÑŒÐ½Ð¾Ð³Ð¾ Ñ€Ð°Ñ†Ð¸Ð¾Ð½Ð°
        </Typography>

        <Stepper activeStep={activeStep} style={{ marginBottom: '32px' }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        {error && (
          <Alert severity="error" style={{ marginBottom: '16px' }}>
            {error}
          </Alert>
        )}

        {saveSuccess && activeStep === 2 && (
          <Alert severity="success" style={{ marginBottom: '16px' }}>
            âœ… ÐŸÐ»Ð°Ð½ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½Ñ‘Ð½ Ð² Ð±Ð°Ð·Ñƒ Ð´Ð°Ð½Ð½Ñ‹Ñ…!
          </Alert>
        )}

        {activeStep === 0 && (
          <Box>
            <Typography variant="h6" gutterBottom style={{ color: '#2E8B57' }}>
              ðŸ“Š ÐŸÐµÑ€ÑÐ¾Ð½Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ
