import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Box,
  TextField,
  Button,
  Typography,
  Alert,
  Container,
  Card,
  CardContent,
  Grid
} from '@mui/material';
import { PersonAdd, Login } from '@mui/icons-material';
import { StorageService } from '../utils/storage';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    age: '',
    weight: '',
    height: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Пароли не совпадают');
      setLoading(false);
      return;
    }

    if (!formData.name || !formData.email || !formData.password) {
      setError('Пожалуйста, заполните все обязательные поля');
      setLoading(false);
      return;
    }

    try {
      // Проверяем, существует ли пользователь с таким email
      const users = await StorageService.getUsers();
      const existingUser = users.find(u => u.email === formData.email);

      if (existingUser) {
        setError('Пользователь с таким email уже существует');
        setLoading(false);
        return;
      }

      // Создаем нового пользователя
      const newUser = {
        name: formData.name,
        email: formData.email,
        password: formData.password, // В реальном приложении нужно хешировать
        age: formData.age ? parseInt(formData.age) : null,
        weight: formData.weight ? parseFloat(formData.weight) : null,
        height: formData.height ? parseFloat(formData.height) : null
      };

      const savedUser = await StorageService.saveUser(newUser);

      // Сохраняем данные в localStorage для быстрого доступа
      localStorage.setItem('isAuthenticated', 'true');
      localStorage.setItem('userId', savedUser.id);
      localStorage.setItem('userName', savedUser.name);
      localStorage.setItem('userEmail', savedUser.email);
      if (savedUser.age) localStorage.setItem('userAge', savedUser.age.toString());
      if (savedUser.weight) localStorage.setItem('userWeight', savedUser.weight.toString());
      if (savedUser.height) localStorage.setItem('userHeight', savedUser.height.toString());

      // Отправляем событие для обновления состояния в шапке
      window.dispatchEvent(new Event('authStateChanged'));

      // Перенаправляем на главную страницу (Dashboard)
      navigate('/', { replace: true });
    } catch (err) {
      console.error('Registration error:', err);
      setError('Ошибка при регистрации');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" style={{ padding: '64px 16px' }}>
      <Card elevation={8} style={{ borderRadius: '12px' }}>
        <CardContent style={{ padding: '32px' }}>
          <Box textAlign="center" style={{ marginBottom: '24px' }}>
            <PersonAdd style={{ fontSize: 48, color: '#2E8B57', marginBottom: '16px' }} />
            <Typography variant="h4" component="h1" gutterBottom style={{ fontWeight: 'bold' }}>
              Регистрация
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Создайте аккаунт для получения персонализированных рационов питания
            </Typography>
          </Box>

          {error && (
            <Alert severity="error" style={{ marginBottom: '16px' }}>
              {error}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Полное имя"
                  margin="normal"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                  placeholder="Иван Иванов"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Email"
                  type="email"
                  margin="normal"
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                  required
                  placeholder="your@email.com"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Возраст"
                  type="number"
                  margin="normal"
                  value={formData.age}
                  onChange={(e) => setFormData({...formData, age: e.target.value})}
                  placeholder="25"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Пароль"
                  type="password"
                  margin="normal"
                  value={formData.password}
                  onChange={(e) => setFormData({...formData, password: e.target.value})}
                  required
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Подтвердите пароль"
                  type="password"
                  margin="normal"
                  value={formData.confirmPassword}
                  onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                  required
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Вес (кг)"
                  type="number"
                  margin="normal"
                  value={formData.weight}
                  onChange={(e) => setFormData({...formData, weight: e.target.value})}
                  placeholder="70"
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="Рост (см)"
                  type="number"
                  margin="normal"
                  value={formData.height}
                  onChange={(e) => setFormData({...formData, height: e.target.value})}
                  placeholder="175"
                />
              </Grid>
            </Grid>

            <Button
              fullWidth
              variant="contained"
              size="large"
              type="submit"
              disabled={loading}
              style={{ 
                marginTop: '24px', 
                padding: '12px',
                backgroundColor: '#2E8B57',
                color: 'white'
              }}
            >
              {loading ? 'Регистрация...' : 'Зарегистрироваться'}
            </Button>

            <Box style={{ textAlign: 'center', marginTop: '24px' }}>
              <Typography variant="body2" color="text.secondary">
                Уже есть аккаунт?{' '}
                <Button
                  component={Link}
                  to="/login"
                  startIcon={<Login />}
                  style={{ textTransform: 'none', color: '#2E8B57' }}
                >
                  Войти
                </Button>
              </Typography>
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Container>
  );
};

export default RegisterPage;
