import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Container,
  Paper,
  Grid,
  Avatar,
  Button,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  ListItemIcon
} from '@mui/material';
import {
  Person,
  Email,
  CalendarToday,
  LocalHospital,
  Restaurant
} from '@mui/icons-material';
import { StorageService } from '../utils/storage';

const ProfilePage = () => {
  const [userData, setUserData] = useState({
    name: localStorage.getItem('userName') || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å',
    email: localStorage.getItem('userEmail') || '',
    age: localStorage.getItem('userAge') || '',
    weight: localStorage.getItem('userWeight') || '',
    height: localStorage.getItem('userHeight') || '',
    joinDate: '–°–µ–≥–æ–¥–Ω—è',
    plansCreated: 0
  });
  const [recentPlans, setRecentPlans] = useState([]);

  useEffect(() => {
    loadUserData();
    loadUserPlans();
  }, []);

  const loadUserData = async () => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      const users = await StorageService.getUsers();
      const user = users.find(u => u.id === userId);
      if (user) {
        setUserData({
          name: user.name,
          email: user.email,
          age: user.age || '',
          weight: user.weight || '',
          height: user.height || '',
          joinDate: user.createdAt ? new Date(user.createdAt).toLocaleDateString('ru-RU') : '–°–µ–≥–æ–¥–Ω—è',
          plansCreated: 0
        });
      }
    }
  };

  const loadUserPlans = async () => {
    const plans = await StorageService.getPlans();
    const userId = localStorage.getItem('userId');
    // –§–∏–ª—å—Ç—Ä—É–µ–º –ø–ª–∞–Ω—ã –ø–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é (–µ—Å–ª–∏ –µ—Å—Ç—å —Å–≤—è–∑—å) –∏–ª–∏ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –≤—Å–µ
    const userPlans = plans.slice(0, 5); // –ü–æ—Å–ª–µ–¥–Ω–∏–µ 5 –ø–ª–∞–Ω–æ–≤
    setRecentPlans(userPlans.map(plan => ({
      id: plan.id,
      name: plan.name,
      date: plan.createdAt ? new Date(plan.createdAt).toLocaleDateString('ru-RU') : '–ù–µ–¥–∞–≤–Ω–æ'
    })));
    setUserData(prev => ({ ...prev, plansCreated: plans.length }));
  };

  return (
    <Container maxWidth="lg" style={{ padding: '32px 0' }}>
      <Typography variant="h4" component="h1" gutterBottom style={{ fontWeight: 'bold' }}>
        üë§ –ü—Ä–æ—Ñ–∏–ª—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      </Typography>

      <Grid container spacing={4}>
        <Grid item xs={12} md={4}>
          <Paper style={{ padding: '24px', textAlign: 'center' }}>
            <Avatar
              style={{
                width: 100,
                height: 100,
                margin: '0 auto 16px auto',
                backgroundColor: '#2E8B57',
                fontSize: '2rem'
              }}
            >
              {userData.name.split(' ').map(n => n[0]).join('')}
            </Avatar>

            <Typography variant="h5" gutterBottom style={{ fontWeight: 'bold' }}>
              {userData.name}
            </Typography>

            <List>
              <ListItem>
                <ListItemIcon>
                  <Email />
                </ListItemIcon>
                <ListItemText primary="Email" secondary={userData.email} />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <CalendarToday />
                </ListItemIcon>
                <ListItemText primary="–î–∞—Ç–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏" secondary={userData.joinDate} />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <LocalHospital />
                </ListItemIcon>
                <ListItemText primary="–û—Å–Ω–æ–≤–Ω–æ–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ" secondary={userData.disease} />
              </ListItem>
