import React, { useState, useEffect } from 'react';
import {
  AppBar, Tabs, Tab, Box, Typography, Container, Card, CardContent,
  Button, CircularProgress, Snackbar, Alert, Dialog, DialogTitle,
  DialogContent, DialogActions, TextField, Table, TableHead, TableRow,
  TableCell, TableBody, Grid, Paper
} from '@mui/material';
import { Activity, Users, FileText, AlertCircle, Plus, RefreshCw, Trash2, Eye } from 'lucide-react';

const API_BASE_URL = 'http://localhost:8080/api';

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalPlans: 0,
    totalDiseases: 0,
    popularDiseases: []
  });

  const [showDiseaseDialog, setShowDiseaseDialog] = useState(false);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [notification, setNotification] = useState({ show: false, message: '', type: 'success' });

  const [newDisease, setNewDisease] = useState({
    name: '',
    description: '',
    protein: 0,
    fat: 0,
    carbs: 0,
    calories: 0,
    vitamins: '',
    recommendations: ''
  });

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    setError(null);
    try {
      await Promise.all([loadUsers(), loadDietPlans(), loadDiseases()]);
    } catch (err) {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/users`);
      if (!res.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π');
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      showNotification('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π', 'error');
    }
  };

  const loadDietPlans = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diet-plans`);
      if (!res.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤');
      const data = await res.json();
      setDietPlans(data);
      calculateStats(data);
    } catch (err) {
      showNotification('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤', 'error');
    }
  };

  const loadDiseases = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diseases`);
      if (!res.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π');
      const data = await res.json();
      setDiseases(data);
    } catch (err) {
      showNotification('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π', 'error');
    }
  };

  const calculateStats = (plans) => {
    const diseaseCounts = {};
    plans.forEach(plan => {
      if (plan.disease) {
        const diseaseName = plan.disease.name || plan.disease;
        diseaseCounts[diseaseName] = (diseaseCounts[diseaseName] || 0) + 1;
      }
    });

    const popularDiseases = Object.entries(diseaseCounts)
      .map(([disease, count]) => ({ disease, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    setStats({
      totalUsers: users.length,
      totalPlans: plans.length,
      totalDiseases: diseases.length,
      popularDiseases
    });
  };

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
  };

  const tabs = [
    { icon: <Activity size={18} />, label: '–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞' },
    { icon: <Users size={18} />, label: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏' },
    { icon: <FileText size={18} />, label: '–†–∞—Ü–∏–æ–Ω—ã' },
    { icon: <AlertCircle size={18} />, label: '–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è' }
  ];

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        üõ†Ô∏è –ü–∞–Ω–µ–ª—å –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
      </Typography>

      <AppBar position="static" color="default" sx={{ borderRadius: 2, mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={(e, val) => setTabValue(val)}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
        >
          {tabs.map((t, i) => (
            <Tab key={i} icon={t.icon} label={t.label} />
          ))}
        </Tabs>
      </AppBar>

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 3 }}>
          <CircularProgress />
        </Box>
      )}

      {error && <Alert severity="error">{error}</Alert>}

      {/* –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ */}
      <TabPanel value={tabValue} index={0}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={4}>
            <Card><CardContent><Typography>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π: {stats.totalUsers}</Typography></CardContent></Card>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Card><CardContent><Typography>–†–∞—Ü–∏–æ–Ω–æ–≤: {stats.totalPlans}</Typography></CardContent></Card>
          </Grid>
          <Grid item xs={12} sm={4}>
            <Card><CardContent><Typography>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–π: {stats.totalDiseases}</Typography></CardContent></Card>
          </Grid>
        </Grid>
      </TabPanel>

      {/* –¢–∞–±–ª–∏—Ü–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π */}
      <TabPanel value={tabValue} index={1}>
        <Paper sx={{ p: 2 }}>
          <Typography variant="h6" gutterBottom>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>–ò–º—è</TableCell>
                <TableCell>Email</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {users.map(u => (
                <TableRow key={u.id}>
                  <TableCell>{u.id}</TableCell>
                  <TableCell>{u.username}</TableCell>
                  <TableCell>{u.email}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Paper>
      </TabPanel>

      {/* Snackbar —É–≤–µ–¥–æ–º–ª–µ–Ω–∏—è */}
      <Snackbar
        open={notification.show}
        autoHideDuration={3000}
        onClose={() => setNotification({ show: false })}
        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
      >
        <Alert severity={notification.type}>{notification.message}</Alert>
      </Snackbar>
    </Container>
  );
};

export default AdminPage;
