sourcesContent:
import React, { useState } from 'react';
import {
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  Grid,
  Alert,
  Container,
  Card,
  CardContent,
  Chip,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  IconButton
} from '@mui/material';
import {
  Search,
  Delete,
  Restaurant,
  Close,
  Visibility
} from '@mui/icons-material';

// База данных рецептов с продуктами
const recipeDatabase = [
  {
    name: 'Омлет с овощами',
    ingredients: ['яйца', 'помидоры', 'лук', 'перец', 'масло'],
    calories: 320,
    protein: 18,
    fat: 22,
    carbs: 12,
    description: 'Вкусный омлет с свежими овощами'
  },
  {
    name: 'Салат из овощей',
    ingredients: ['помидоры', 'огурцы', 'лук', 'масло', 'зелень'],
    calories: 150,
    protein: 3,
    fat: 10,
    carbs: 15,
    description: 'Свежий овощной салат'
  },
  {
    name: 'Куриная грудка с рисом',
    ingredients: ['курица', 'рис', 'лук', 'морковь'],
    calories: 450,
    protein: 35,
    fat: 12,
    carbs: 45,
    description: 'Диетическое блюдо с курицей и рисом'
  },
  {
    name: 'Гречка с грибами',
    ingredients: ['гречка', 'грибы', 'лук', 'масло'],
    calories: 380,
    protein: 12,
    fat: 15,
    carbs: 55,
    description: 'Сытное блюдо из гречки и грибов'
  },
  {
    name: 'Творожная запеканка',
    ingredients: ['творог', 'яйца', 'мука', 'сахар'],
    calories: 280,
    protein: 20,
    fat: 10,
    carbs: 25,
    description: 'Нежная творожная запеканка'
  },
  {
    name: 'Овощной суп',
    ingredients: ['картофель', 'морковь', 'лук', 'капуста', 'помидоры'],
    calories: 200,
    protein: 5,
    fat: 5,
    carbs: 35,
    description: 'Легкий овощной суп'
  },
  {
    name: 'Рыба на пару с овощами',
    ingredients: ['рыба', 'брокколи', 'морковь', 'кабачок'],
    calories: 250,
    protein: 28,
    fat: 8,
    carbs: 15,
    description: 'Полезная рыба с овощами на пару'
  },
  {
    name: 'Паста с овощами',
    ingredients: ['макароны', 'помидоры', 'перец', 'лук', 'чеснок'],
    calories: 420,
    protein: 15,
    fat: 12,
    carbs: 65,
    description: 'Итальянская паста с овощами'
  },
  {
    name: 'Тушеная капуста с мясом',
    ingredients: ['капуста', 'мясо', 'морковь', 'лук', 'томаты'],
    calories: 380,
    protein: 25,
    fat: 18,
    carbs: 28,
    description: 'Сытное блюдо из капусты и мяса'
  },
  {
    name: 'Сырники',
    ingredients: ['творог', 'яйца', 'мука', 'сахар'],
    calories: 320,
    protein: 22,
    fat: 12,
    carbs: 30,
    description: 'Классические сырники'
  },
  {
    name: 'Овощное рагу',
    ingredients: ['кабачок', 'баклажан', 'помидоры', 'перец', 'лук'],
    calories: 180,
    protein: 5,
    fat: 8,
    carbs: 25,
    description: 'Ароматное овощное рагу'
  },
  {
    name: 'Куриный суп',
    ingredients: ['курица', 'картофель', 'морковь', 'лук', 'лапша'],
    calories: 350,
    protein: 22,
    fat: 10,
    carbs: 40,
    description: 'Наваристый куриный суп'
  }
];

const RecipeGeneratorPage = () => {
  const [productInput, setProductInput] = useState('');
  const [products, setProducts] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);

  const normalizeProduct = (product) => {
    return product.toLowerCase().trim();
  };

  const addProduct = () => {
    const normalized = normalizeProduct(productInput);
    if (normalized && !products.includes(normalized)) {
      if (products.length >= 20) {
        setError('Слишком много продуктов! Максимум 20.');
        return;
      }
      setProducts([...products, normalized]);
      setProductInput('');
      setError('');
    }
  };

  const removeProduct = (product) => {
    setProducts(products.filter(p => p !== product));
  };

  const findRecipes = () => {
    if (products.length === 0) {
      setError('Добавьте хотя бы один продукт');
      return;
    }

    if (products.length > 15) {
      setError('Список продуктов слишком большой. Пожалуйста, используйте не более 15 продуктов.');
      setRecipes([]);
      return;
    }

    setLoading(true);
    setError('');

    // Имитация AI поиска (в реальности здесь был бы API вызов)
    setTimeout(() => {
      const foundRecipes = recipeDatabase.filter(recipe => {
        // Проверяем, есть ли хотя бы 2 продукта из рецепта в списке пользователя
        const matchingIngredients = recipe.ingredients.filter(ingredient =>
          products.some(product => 
            ingredient.includes(product) || product.includes(ingredient)
          )
        );
        return matchingIngredients.length >= 2;
      });

      if (foundRecipes.length === 0) {
        setError('Рецепты не найдены. Попробуйте изменить список продуктов.');
        setRecipes([]);
      } else {
        setRecipes(foundRecipes);
        setError('');
      }

      setLoading(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addProduct();
    }
  };

  const handleOpenRecipe = (recipe) => {
    setSelectedRecipe(recipe);
    setDialogOpen(true);
  };

  const handleCloseRecipe = () => {
    setDialogOpen(false);
    setSelectedRecipe(null);
  };

  return (
    <Container maxWidth="lg" style={{ padding: '32px 0' }} className="fade-in-up">
      <Paper elevation={3} style={{ 
        padding: '32px', 
        marginBottom: '32px',
        animation: 'fadeInUp 0.8s ease-out',
        borderRadius: '24px'
      }}>
        <Typography variant="h4" component="h1" gutterBottom align="center" style={{ color: '#2E8B57' }}>
          🤖 AI Генерация рецептов
        </Typography>
        <Typography variant="body1" color="text.secondary" align="center" style={{ marginBottom: '32px' }}>
          Введите список продуктов, которые есть у вас дома, и мы подберем подходящие рецепты
        </Typography>

        {error && (
          <Alert severity={error.includes('не найден') ? 'warning' : 'error'} style={{ marginBottom: '16px' }}>
            {error}
          </Alert>
        )}

        <Box style={{ marginBottom: '24px' }}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={8}>
              <TextField
                fullWidth
                label="Добавить продукт"
                value={productInput}
                onChange={(e) => setProductInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Например: яйца, курица, помидоры..."
                helperText="Нажмите Enter или кнопку для добавления"
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <Button
                fullWidth
                variant="contained"
                onClick={addProduct}
                style={{ backgroundColor: '#2E8B57', height: '56px' }}
              >
                Добавить
              </Button>
            </Grid>
          </Grid>
        </Box>

        {products.length > 0 && (
          <Box style={{ marginBottom: '24px' }}>
            <Typography variant="h6" gutterBottom>
              Ваши продукты ({products.length}/20):
            </Typography>
            <Box style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
              {products.map((product, index) => (
                <Chip
                  key={index}
                  label={product}
                  onDelete={() => removeProduct(product)}
                  deleteIcon={<Delete />}
                  style={{ backgroundColor: '#E8F5E9', color: '#2E8B57' }}
                />
              ))}
            </Box>
          </Box>
        )}

        <Box style={{ textAlign: 'center', marginBottom: '32px' }}>
          <Button
            variant="contained"
            size="large"
            onClick={findRecipes}
            disabled={loading || products.length === 0}
            startIcon={loading ? <CircularProgress size={20} /> : <Search />}
            style={{ 
              backgroundColor: '#2E8B57',
              padding: '12px 48px',
              fontSize: '1.1rem'
            }}
          >
            {loading ? 'Поиск рецептов...' : 'Подобрать блюда'}
          </Button>
        </Box>

        {recipes.length > 0 && (
          <Box>
            <Typography variant="h5" gutterBottom style={{ color: '#2E8B57', marginBottom: '24px' }}>
              🍽️ Найденные рецепты ({recipes.length})
            </Typography>
            <Grid container spacing={3}>
              {recipes.map((recipe, index) => (
                <Grid 
                  item 
                  xs={12} 
                  md={6} 
                  key={index}
                  style={{
                    animation: `fadeInUp 0.6s ease-out ${index * 0.15}s both`
                  }}
                >
                  <Card 
                    style={{ 
                      height: '100%', 
                      borderLeft: '4px solid', 
                      borderColor: '#2E8B57',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease'
                    }}
                    onClick={() => handleOpenRecipe(recipe)}
                    sx={{
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: '0 12px 24px rgba(0,0,0,0.15)',
                      }
                    }}
                  >
                    <CardContent>
                      <Box style={{ display: 'flex', alignItems: 'start', marginBottom: '16px' }}>
                        <Restaurant style={{ fontSize: 32, color: '#2E8B57', marginRight: '12px' }} />
                        <Box style={{ flex: 1 }}>
                          <Box style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                            <Typography variant="h6" gutterBottom style={{ color: '#2E8B57', fontWeight: 600 }}>
                              {recipe.name}
                            </Typography>
                            <Button
                              size="small"
                              startIcon={<Visibility />}
                              onClick={(e) => {
                                e.stopPropagation();
                                handleOpenRecipe(recipe);
                              }}
                              style={{ color: '#2E8B57' }}
                            >
                              Открыть
                            </Button>
                          </Box>
                          <Typography variant="body2" color="text.secondary" style={{ marginBottom: '12px' }}>
                            {recipe.description}
                          </Typography>
                        </Box>
                      </Box>

                      <Typography variant="subtitle2" gutterBottom>
                        Ингредиенты:
                      </Typography>
                      <Box style={{ display: 'flex', flexWrap: 'wrap', gap: '4px', marginBottom: '16px' }}>
                        {recipe.ingredients.map((ingredient, idx) => (
                          <Chip
                            key={idx}
                            label={ingredient}
                            size="small"
                            variant="outlined"
                            style={{ fontSize: '0.75rem' }}
                          />
                        ))}
                      </Box>

                      <Grid container spacing={2}>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#ED8936', fontWeight: 'bold' }}>
                              {recipe.calories}
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              ккал
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#4299E1', fontWeight: 'bold' }}>
                              {recipe.protein}г
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              Белки
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#38A169', fontWeight: 'bold' }}>
                              {recipe.fat}г
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              Жиры
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#ED8936', fontWeight: 'bold' }}>
                              {recipe.carbs}г
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              Углеводы
                            </Typography>
                          </Box>
                        </Grid>
                      </Grid>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}

        {/* Диалог детального просмотра рецепта */}
        <Dialog 
          open={dialogOpen} 
          onClose={handleCloseRecipe}
          maxWidth="md"
          fullWidth
          PaperProps={{
            style: {
              borderRadius: '28px',
              padding: '0',
              overflow: 'hidden',
              boxShadow: '0 25px 50px rgba(0,0,0,0.25)'
            }
          }}
        >
          <DialogTitle style={{ 
            background: 'linear-gradient(135deg, #10B981 0%, #059669 50%, #047857 100%)',
            color: 'white',
            padding: '28px 32px',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            position: 'relative',
            overflow: 'hidden'
          }}>
            {/* Декоративные элементы */}
            <Box style={{
              position: 'absolute',
              top: '-50px',
              right: '-50px',
              width: '200px',
              height: '200px',
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.1)',
              filter: 'blur(30px)'
            }} />
            <Box style={{
              position: 'absolute',
              bottom: '-30px',
              left: '-30px',
              width: '150px',
              height: '150px',
              borderRadius: '50%',
              background: 'rgba(255,255,255,0.08)',
              filter: 'blur(25px)'
            }} />
            
            <Box style={{ display: 'flex', alignItems: 'center', gap: '16px', position: 'relative', zIndex: 1 }}>
              <Box style={{
                background: 'rgba(255,255,255,0.2)',
                borderRadius: '16px',
                padding: '12px',
                backdropFilter: 'blur(10px)'
              }}>
                <Restaurant style={{ fontSize: 36 }} />
              </Box>
              <Typography variant="h4" style={{ fontWeight: 800, letterSpacing: '-0.02em' }}>
                {selectedRecipe?.name}
              </Typography>
            </Box>
            <IconButton 
              onClick={handleCloseRecipe} 
              style={{ 
                color: 'white',
                background: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                position: 'relative',
                zIndex: 1
              }}
              sx={{
                '&:hover': {
                  background: 'rgba(255,255,255,0.3)',
                  transform: 'rotate(90deg)',
                  transition: 'all 0.3s ease'
                }
              }}
            >
              <Close />
            </IconButton>
          </DialogTitle>
          <DialogContent style={{ padding: '32px', background: 'linear-gradient(to bottom, #FAFAFA 0%, #FFFFFF 100%)' }}>
            {selectedRecipe && (
              <Box>
                <Box style={{
                  background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(59, 130, 246, 0.1) 100%)',
                  borderRadius: '16px',
                  padding: '20px',
                  marginBottom: '28px',
                  border: '1px solid rgba(16, 185, 129, 0.2)'
                }}>
                  <Typography variant="body1" style={{ 
                    fontSize: '1.15rem', 
                    lineHeight: 1.7,
                    color: '#374151',
                    fontWeight: 500
                  }}>
                    {selectedRecipe.description}
                  </Typography>
                </Box>

                <Box style={{ marginBottom: '32px' }}>
                  <Typography variant="h5" gutterBottom style={{ 
                    fontWeight: 700, 
                    marginBottom: '20px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    color: '#1F2937'
                  }}>
                    <Box style={{
                      background: 'linear-gradient(135deg, #10B981 0%, #059669 100%)',
                      borderRadius: '12px',
                      padding: '8px 12px',
                      color: 'white',
                      fontSize: '1.2rem'
                    }}>
                      📋
                    </Box>
                    Ингредиенты:
                  </Typography>
                  <Box style={{ 
                    display: 'flex', 
                    flexWrap: 'wrap', 
                    gap: '12px'
                  }}>
                    {selectedRecipe.ingredients.map((ingredient, idx) => (
                      <Chip
                        key={idx}
                        label={ingredient}
                        style={{ 
                          background: 'linear-gradient(135deg, #E8F5E9 0%, #D1FAE5 100%)',
                          color: '#047857',
                          fontSize: '1rem',
                          padding: '8px 16px',
                          height: 'auto',
                          fontWeight: 600,
                          border: '2px solid rgba(16, 185, 129, 0.2)',
                          boxShadow: '0 4px 12px rgba(16, 185, 129, 0.15)'
                        }}
                        sx={{
                          '&:hover': {
                            transform: 'translateY(-2px)',
                            boxShadow: '0 6px 16px rgba(16, 185, 129, 0.25)',
                            transition: 'all 0.3s ease'
                          }
                        }}
                      />
                    ))}
                  </Box>
                </Box>

                <Box>
                  <Typography variant="h5" gutterBottom style={{ 
                    fontWeight: 700, 
                    marginBottom: '24px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    color: '#1F2937'
                  }}>
                    <Box style={{
                      background: 'linear-gradient(135deg, #F59E0B 0%, #D97706 100%)',
                      borderRadius: '12px',
                      padding: '8px 12px',
                      color: 'white',
                      fontSize: '1.2rem'
                    }}>
                      🥗
                    </Box>
                    Пищевая ценность:
                  </Typography>
                  <Grid container spacing={3}>
                    <Grid item xs={6} md={3}>
                      <Card style={{ 
                        background: 'linear-gradient(135deg, #F59E0B 0%, #D97706 100%)',
                        color: 'white',
                        textAlign: 'center',
                        padding: '28px 20px',
                        borderRadius: '20px',
                        boxShadow: '0 10px 30px rgba(245, 158, 11, 0.3)',
                        border: '2px solid rgba(255,255,255,0.2)',
                        transition: 'all 0.3s ease',
                        position: 'relative',
                        overflow: 'hidden'
                      }}
                      sx={{
                        '&:hover': {
                          transform: 'translateY(-8px) scale(1.05)',
                          boxShadow: '0 20px 40px rgba(245, 158, 11, 0.4)',
                        }
                      }}
                      >
                        <Box style={{
                          position: 'absolute',
                          top: '-20px',
                          right: '-20px',
                          width: '80px',
                          height: '80px',
                          borderRadius: '50%',
                          background: 'rgba(255,255,255,0.2)',
                          filter: 'blur(20px)'
                        }} />
                        <Typography variant="h3" style={{ 
                          fontWeight: 900, 
                          marginBottom: '8px',
                          position: 'relative',
                          zIndex: 1,
                          textShadow: '0 2px 10px rgba(0,0,0,0.2)'
                        }}>
                          {selectedRecipe.calories}
                        </Typography>
                        <Typography variant="body1" style={{ 
                          opacity: 0.95,
                          fontWeight: 600,
                          position: 'relative',
                          zIndex: 1
                        }}>
                          ккал
                        </Typography>
                      </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Card style={{ 
                        background: 'linear-gradient(135deg, #3B82F6 0%, #2563EB 100%)',
                        color: 'white',
                        textAlign: 'center',
                        padding: '28px 20px',
                        borderRadius: '20px',
                        boxShadow: '0 10px 30px rgba(59, 130, 246, 0.3)',
                        border: '2px solid rgba(255,255,255,0.2)',
                        transition: 'all 0.3s ease',
                        position: 'relative',
                        overflow: 'hidden'
                      }}
                      sx={{
                        '&:hover': {
                          transform: 'translateY(-8px) scale(1.05)',
                          boxShadow: '0 20px 40px rgba(59, 130, 246, 0.4)',
                        }
                      }}
                      >
                        <Box style={{
                          position: 'absolute',
                          top: '-20px',
                          right: '-20px',
                          width: '80px',
                          height: '80px',
                          borderRadius: '50%',
                          background: 'rgba(255,255,255,0.2)',
                          filter: 'blur(20px)'
                        }} />
                        <Typography variant="h3" style={{ 
                          fontWeight: 900, 
                          marginBottom: '8px',
                          position: 'relative',
                          zIndex: 1,
                          textShadow: '0 2px 10px rgba(0,0,0,0.2)'
                        }}>
                          {selectedRecipe.protein}г
                        </Typography>
                        <Typography variant="body1" style={{ 
                          opacity: 0.95,
                          fontWeight: 600,
                          position: 'relative',
                          zIndex: 1
                        }}>
                          Белки
                        </Typography>
                      </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Card style={{ 
                        background: 'linear-gradient(135deg, #10B981 0%, #059669 100%)',
                        color: 'white',
                        textAlign: 'center',
                        padding: '28px 20px',
                        borderRadius: '20px',
                        boxShadow: '0 10px 30px rgba(16, 185, 129, 0.3)',
                        border: '2px solid rgba(255,255,255,0.2)',
                        transition: 'all 0.3s ease',
                        position: 'relative',
                        overflow: 'hidden'
                      }}
                      sx={{
                        '&:hover': {
                          transform: 'translateY(-8px) scale(1.05)',
                          boxShadow: '0 20px 40px rgba(16, 185, 129, 0.4)',
                        }
                      }}
                      >
                        <Box style={{
                          position: 'absolute',
                          top: '-20px',
                          right: '-20px',
                          width: '80px',
                          height: '80px',
                          borderRadius: '50%',
                          background: 'rgba(255,255,255,0.2)',
                          filter: 'blur(20px)'
                        }} />
                        <Typography variant="h3" style={{ 
                          fontWeight: 900, 
                          marginBottom: '8px',
                          position: 'relative',
                          zIndex: 1,
                          textShadow: '0 2px 10px rgba(0,0,0,0.2)'
                        }}>
                          {selectedRecipe.fat}г
                        </Typography>
                        <Typography variant="body1" style={{ 
                          opacity: 0.95,
                          fontWeight: 600,
                          position: 'relative',
                          zIndex: 1
                        }}>
                          Жиры
                        </Typography>
                      </Card>
                    </Grid>
                    <Grid item xs={6} md={3}>
                      <Card style={{ 
                        background: 'linear-gradient(135deg, #EF4444 0%, #DC2626 100%)',
                        color: 'white',
                        textAlign: 'center',
                        padding: '28px 20px',
                        borderRadius: '20px',
                        boxShadow: '0 10px 30px rgba(239, 68, 68, 0.3)',
                        border: '2px solid rgba(255,255,255,0.2)',
                        transition: 'all 0.3s ease',
                        position: 'relative',
                        overflow: 'hidden'
                      }}
                      sx={{
                        '&:hover': {
                          transform: 'translateY(-8px) scale(1.05)',
                          boxShadow: '0 20px 40px rgba(239, 68, 68, 0.4)',
                        }
                      }}
                      >
                        <Box style={{
                          position: 'absolute',
                          top: '-20px',
                          right: '-20px',
                          width: '80px',
                          height: '80px',
                          borderRadius: '50%',
                          background: 'rgba(255,255,255,0.2)',
                          filter: 'blur(20px)'
                        }} />
                        <Typography variant="h3" style={{ 
                          fontWeight: 900, 
                          marginBottom: '8px',
                          position: 'relative',
                          zIndex: 1,
                          textShadow: '0 2px 10px rgba(0,0,0,0.2)'
                        }}>
                          {selectedRecipe.carbs}г
                        </Typography>
                        <Typography variant="body1" style={{ 
                          opacity: 0.95,
                          fontWeight: 600,
                          position: 'relative',
                          zIndex: 1
                        }}>
                          Углеводы
                        </Typography>
                      </Card>
                    </Grid>
                  </Grid>
                </Box>
              </Box>
            )}
          </DialogContent>
          <DialogActions style={{ 
            padding: '24px 32px', 
            justifyContent: 'center',
            background: 'linear-gradient(to top, #F9FAFB 0%, #FFFFFF 100%)',
            borderTop: '1px solid rgba(0,0,0,0.05)'
          }}>
            <Button 
              onClick={handleCloseRecipe}
              variant="contained"
              style={{
                background: 'linear-gradient(135deg, #10B981 0%, #059669 100%)',
                color: 'white',
                borderRadius: '16px',
                padding: '14px 48px',
                fontWeight: 700,
                fontSize: '1.1rem',
                boxShadow: '0 8px 24px rgba(16, 185, 129, 0.3)',
                textTransform: 'none',
                transition: 'all 0.3s ease'
              }}
              sx={{
                '&:hover': {
                  transform: 'translateY(-2px)',
                  boxShadow: '0 12px 32px rgba(16, 185, 129, 0.4)',
                  background: 'linear-gradient(135deg, #059669 0%, #047857 100%)',
                }
              }}
            >
              Закрыть
            </Button>
          </DialogActions>
        </Dialog>
      </Paper>
    </Container>
  );
};

export default RecipeGeneratorPage;
