import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Container
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';

const AppHeader = () => {
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    // –ü–ª–∞–≤–Ω–∞—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∞ –≤–≤–µ—Ä—Ö –ø–µ—Ä–µ–¥ –ø–µ—Ä–µ—Ö–æ–¥–æ–º
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
    // –ù–µ–±–æ–ª—å—à–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ –¥–ª—è –ø–ª–∞–≤–Ω–æ—Å—Ç–∏
    setTimeout(() => {
      navigate(path);
    }, 200);
  };

  return (
    <AppBar 
      position="sticky"
      elevation={0}
      style={{ 
        background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.98) 0%, rgba(240, 253, 250, 0.98) 100%)',
        backdropFilter: 'blur(30px) saturate(180%)',
        boxShadow: '0 4px 20px rgba(16, 185, 129, 0.08), 0 0 0 1px rgba(16, 185, 129, 0.05)',
        borderBottom: '1px solid rgba(16, 185, 129, 0.1)',
        position: 'relative',
        overflow: 'hidden'
      }}
    >
      {/* –î–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã */}
      <Box style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '100%',
        background: 'linear-gradient(90deg, transparent 0%, rgba(16, 185, 129, 0.03) 50%, transparent 100%)',
        pointerEvents: 'none'
      }} />
      <Box style={{
        position: 'absolute',
        top: '-50%',
        right: '-10%',
        width: '300px',
        height: '300px',
        borderRadius: '50%',
        background: 'radial-gradient(circle, rgba(16, 185, 129, 0.08) 0%, transparent 70%)',
        filter: 'blur(40px)',
        pointerEvents: 'none'
      }} />
      
      <Container maxWidth="xl">
        <Toolbar style={{ justifyContent: 'space-between', padding: '16px 0', position: 'relative', zIndex: 1 }}>
          <Typography
            variant="h4"
            component={Link}
            to="/"
            style={{
              fontWeight: 900,
              textDecoration: 'none',
              background: 'linear-gradient(135deg, #10B981 0%, #059669 50%, #3B82F6 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text',
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              fontSize: '1.85rem',
              letterSpacing: '-0.02em',
              transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
              position: 'relative'
            }}
            sx={{
              '&:hover': {
                transform: 'scale(1.05)',
                filter: 'brightness(1.1)',
              },
              '&::after': {
                content: '""',
                position: 'absolute',
                bottom: '-4px',
                left: 0,
                width: '0%',
                height: '3px',
                background: 'linear-gradient(90deg, #10B981 0%, #3B82F6 100%)',
                borderRadius: '2px',
                transition: 'width 0.4s ease'
              },
              '&:hover::after': {
                width: '100%'
              }
            }}
          >
            <Box style={{
              background: 'linear-gradient(135deg, #10B981 0%, #059669 100%)',
              borderRadius: '12px',
              padding: '6px 10px',
              display: 'flex',
              alignItems: 'center',
              boxShadow: '0 4px 12px rgba(16, 185, 129, 0.25)'
            }}>
              üå±
            </Box>
            NutriPlanner
          </Typography>

          <Box style={{ display: 'flex', gap: '8px', alignItems: 'center', flexWrap: 'wrap' }}>
            <Button 
              onClick={() => handleNavigation('/generator')}
              variant="contained"
              style={{ 
                background: 'linear-gradient(135deg, #10B981 0%, #059669 100%)',
                color: 'white',
                fontWeight: 700,
                borderRadius: '14px',
                padding: '10px 24px',
                boxShadow: '0 6px 20px rgba(16, 185, 129, 0.35), inset 0 1px 0 rgba(255,255,255,0.2)',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                textTransform: 'none',
                fontSize: '0.95rem',
                letterSpacing: '0.01em',
                position: 'relative',
                overflow: 'hidden'
              }}
              sx={{
                '&::before': {
                  content: '""',
                  position: 'absolute',
                  top: 0,
                  left: '-100%',
                  width: '100%',
                  height: '100%',
                  background: 'linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent)',
                  transition: 'left 0.5s ease'
                },
                '&:hover::before': {
                  left: '100%'
                },
                '&:hover': {
                  transform: 'translateY(-3px) scale(1.02)',
                  boxShadow: '0 10px 28px rgba(16, 185, 129, 0.45), inset 0 1px 0 rgba(255,255,255,0.3)',
                  background: 'linear-gradient(135deg, #059669 0%, #047857 100%)',
                },
                '&:active': {
                  transform: 'translateY(-1px) scale(0.98)',
                }
              }}
            >
              ‚ú® –°–æ–∑–¥–∞—Ç—å —Ä–∞—Ü–∏–æ–Ω
            </Button>
            <Button 
              onClick={() => handleNavigation('/plans')}
              style={{ 
                color: '#374151',
                fontWeight: 600,
                borderRadius: '12px',
                padding: '10px 18px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                textTransform: 'none',
                fontSize: '0.95rem',
                position: 'relative'
              }}
              sx={{
                '&:hover': {
                  background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.12) 0%, rgba(59, 130, 246, 0.08) 100%)',
                  color: '#10B981',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 12px rgba(16, 185, 129, 0.15)',
                }
              }}
            >
              üìã –ú–æ–∏ –ø–ª–∞–Ω—ã
            </Button>
            <Button 
              onClick={() => handleNavigation('/progress')}
              style={{ 
                color: '#374151',
                fontWeight: 500,
                borderRadius: '12px',
                padding: '8px 16px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
              sx={{
                '&:hover': {
                  background: 'rgba(16, 185, 129, 0.1)',
                  color: '#10B981',
                  transform: 'translateY(-2px)',
                }
              }}
            >
              –ü—Ä–æ–≥—Ä–µ—Å—Å
            </Button>
            <Button 
              onClick={() => handleNavigation('/profile')}
              style={{ 
                color: '#374151',
                fontWeight: 500,
                borderRadius: '12px',
                padding: '8px 16px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
              sx={{
                '&:hover': {
                  background: 'rgba(16, 185, 129, 0.1)',
                  color: '#10B981',
                  transform: 'translateY(-2px)',
                }
              }}
            >
              –ü—Ä–æ—Ñ–∏–ª—å
            </Button>
            <Button 
              onClick={() => handleNavigation('/recipe-generator')}
              style={{ 
                color: '#374151',
                fontWeight: 500,
                borderRadius: '12px',
                padding: '8px 16px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
              sx={{
                '&:hover': {
                  background: 'rgba(16, 185, 129, 0.1)',
                  color: '#10B981',
                  transform: 'translateY(-2px)',
                }
              }}
            >
              –†–µ—Ü–µ–ø—Ç—ã
            </Button>
            <Button 
              onClick={() => handleNavigation('/seasonal-menu')}
              style={{ 
                color: '#374151',
                fontWeight: 500,
                borderRadius: '12px',
                padding: '8px 16px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
              sx={{
                '&:hover': {
                  background: 'rgba(16, 185, 129, 0.1)',
                  color: '#10B981',
                  transform: 'translateY(-2px)',
                }
              }}
            >
              –°–µ–∑–æ–Ω—ã
            </Button>
            <Button 
              onClick={() => handleNavigation('/login')}
              variant="outlined"
              style={{ 
                borderColor: '#10B981',
                color: '#10B981',
                fontWeight: 600,
                borderRadius: '12px',
                padding: '8px 20px',
                transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
              }}
              sx={{
                '&:hover': {
                  background: '#10B981',
                  color: 'white',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 14px rgba(16, 185, 129, 0.3)',
                }
              }}
            >
              –í–æ–π—Ç–∏
            </Button>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default AppHeader;
