// –ë–∞–∑–∞ –¥–∞–Ω–Ω—ã—Ö —Ä–µ—Ü–µ–ø—Ç–æ–≤ —Å –ø—Ä–æ–¥—É–∫—Ç–∞–º–∏
const recipeDatabase = [
  {
    name: '–û–º–ª–µ—Ç —Å –æ–≤–æ—â–∞–º–∏',
    ingredients: ['—è–π—Ü–∞', '–ø–æ–º–∏–¥–æ—Ä—ã', '–ª—É–∫', '–ø–µ—Ä–µ—Ü', '–º–∞—Å–ª–æ'],
    calories: 320,
    protein: 18,
    fat: 22,
    carbs: 12,
    description: '–í–∫—É—Å–Ω—ã–π –æ–º–ª–µ—Ç —Å —Å–≤–µ–∂–∏–º–∏ –æ–≤–æ—â–∞–º–∏'
  },
  {
    name: '–°–∞–ª–∞—Ç –∏–∑ –æ–≤–æ—â–µ–π',
    ingredients: ['–ø–æ–º–∏–¥–æ—Ä—ã', '–æ–≥—É—Ä—Ü—ã', '–ª—É–∫', '–º–∞—Å–ª–æ', '–∑–µ–ª–µ–Ω—å'],
    calories: 150,
    protein: 3,
    fat: 10,
    carbs: 15,
    description: '–°–≤–µ–∂–∏–π –æ–≤–æ—â–Ω–æ–π —Å–∞–ª–∞—Ç'
  },
  {
    name: '–ö—É—Ä–∏–Ω–∞—è –≥—Ä—É–¥–∫–∞ —Å —Ä–∏—Å–æ–º',
    ingredients: ['–∫—É—Ä–∏—Ü–∞', '—Ä–∏—Å', '–ª—É–∫', '–º–æ—Ä–∫–æ–≤—å'],
    calories: 450,
    protein: 35,
    fat: 12,
    carbs: 45,
    description: '–î–∏–µ—Ç–∏—á–µ—Å–∫–æ–µ –±–ª—é–¥–æ —Å –∫—É—Ä–∏—Ü–µ–π –∏ —Ä–∏—Å–æ–º'
  },
  {
    name: '–ì—Ä–µ—á–∫–∞ —Å –≥—Ä–∏–±–∞–º–∏',
    ingredients: ['–≥—Ä–µ—á–∫–∞', '–≥—Ä–∏–±—ã', '–ª—É–∫', '–º–∞—Å–ª–æ'],
    calories: 380,
    protein: 12,
    fat: 15,
    carbs: 55,
    description: '–°—ã—Ç–Ω–æ–µ –±–ª—é–¥–æ –∏–∑ –≥—Ä–µ—á–∫–∏ –∏ –≥—Ä–∏–±–æ–≤'
  },
  {
    name: '–¢–≤–æ—Ä–æ–∂–Ω–∞—è –∑–∞–ø–µ–∫–∞–Ω–∫–∞',
    ingredients: ['—Ç–≤–æ—Ä–æ–≥', '—è–π—Ü–∞', '–º—É–∫–∞', '—Å–∞—Ö–∞—Ä'],
    calories: 280,
    protein: 20,
    fat: 10,
    carbs: 25,
    description: '–ù–µ–∂–Ω–∞—è —Ç–≤–æ—Ä–æ–∂–Ω–∞—è –∑–∞–ø–µ–∫–∞–Ω–∫–∞'
  },
  {
    name: '–û–≤–æ—â–Ω–æ–π —Å—É–ø',
    ingredients: ['–∫–∞—Ä—Ç–æ—Ñ–µ–ª—å', '–º–æ—Ä–∫–æ–≤—å', '–ª—É–∫', '–∫–∞–ø—É—Å—Ç–∞', '–ø–æ–º–∏–¥–æ—Ä—ã'],
    calories: 200,
    protein: 5,
    fat: 5,
    carbs: 35,
    description: '–õ–µ–≥–∫–∏–π –æ–≤–æ—â–Ω–æ–π —Å—É–ø'
  },
  {
    name: '–†—ã–±–∞ –Ω–∞ –ø–∞—Ä—É —Å –æ–≤–æ—â–∞–º–∏',
    ingredients: ['—Ä—ã–±–∞', '–±—Ä–æ–∫–∫–æ–ª–∏', '–º–æ—Ä–∫–æ–≤—å', '–∫–∞–±–∞—á–æ–∫'],
    calories: 250,
    protein: 28,
    fat: 8,
    carbs: 15,
    description: '–ü–æ–ª–µ–∑–Ω–∞—è —Ä—ã–±–∞ —Å –æ–≤–æ—â–∞–º–∏ –Ω–∞ –ø–∞—Ä—É'
  },
  {
    name: '–ü–∞—Å—Ç–∞ —Å –æ–≤–æ—â–∞–º–∏',
    ingredients: ['–º–∞–∫–∞—Ä–æ–Ω—ã', '–ø–æ–º–∏–¥–æ—Ä—ã', '–ø–µ—Ä–µ—Ü', '–ª—É–∫', '—á–µ—Å–Ω–æ–∫'],
    calories: 420,
    protein: 15,
    fat: 12,
    carbs: 65,
    description: '–ò—Ç–∞–ª—å—è–Ω—Å–∫–∞—è –ø–∞—Å—Ç–∞ —Å –æ–≤–æ—â–∞–º–∏'
  },
  {
    name: '–¢—É—à–µ–Ω–∞—è –∫–∞–ø—É—Å—Ç–∞ —Å –º—è—Å–æ–º',
    ingredients: ['–∫–∞–ø—É—Å—Ç–∞', '–º—è—Å–æ', '–º–æ—Ä–∫–æ–≤—å', '–ª—É–∫', '—Ç–æ–º–∞—Ç—ã'],
    calories: 380,
    protein: 25,
    fat: 18,
    carbs: 28,
    description: '–°—ã—Ç–Ω–æ–µ –±–ª—é–¥–æ –∏–∑ –∫–∞–ø—É—Å—Ç—ã –∏ –º—è—Å–∞'
  },
  {
    name: '–°—ã—Ä–Ω–∏–∫–∏',
    ingredients: ['—Ç–≤–æ—Ä–æ–≥', '—è–π—Ü–∞', '–º—É–∫–∞', '—Å–∞—Ö–∞—Ä'],
    calories: 320,
    protein: 22,
    fat: 12,
    carbs: 30,
    description: '–ö–ª–∞—Å—Å–∏—á–µ—Å–∫–∏–µ —Å—ã—Ä–Ω–∏–∫–∏'
  },
  {
    name: '–û–≤–æ—â–Ω–æ–µ —Ä–∞–≥—É',
    ingredients: ['–∫–∞–±–∞—á–æ–∫', '–±–∞–∫–ª–∞–∂–∞–Ω', '–ø–æ–º–∏–¥–æ—Ä—ã', '–ø–µ—Ä–µ—Ü', '–ª—É–∫'],
    calories: 180,
    protein: 5,
    fat: 8,
    carbs: 25,
    description: '–ê—Ä–æ–º–∞—Ç–Ω–æ–µ –æ–≤–æ—â–Ω–æ–µ —Ä–∞–≥—É'
  },
  {
    name: '–ö—É—Ä–∏–Ω—ã–π —Å—É–ø',
    ingredients: ['–∫—É—Ä–∏—Ü–∞', '–∫–∞—Ä—Ç–æ—Ñ–µ–ª—å', '–º–æ—Ä–∫–æ–≤—å', '–ª—É–∫', '–ª–∞–ø—à–∞'],
    calories: 350,
    protein: 22,
    fat: 10,
    carbs: 40,
    description: '–ù–∞–≤–∞—Ä–∏—Å—Ç—ã–π –∫—É—Ä–∏–Ω—ã–π —Å—É–ø'
  }
];

const RecipeGeneratorPage = () => {
  const [productInput, setProductInput] = useState('');
  const [products, setProducts] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const normalizeProduct = (product) => {
    return product.toLowerCase().trim();
  };

  const addProduct = () => {
    const normalized = normalizeProduct(productInput);
    if (normalized && !products.includes(normalized)) {
      if (products.length >= 20) {
        setError('–°–ª–∏—à–∫–æ–º –º–Ω–æ–≥–æ –ø—Ä–æ–¥—É–∫—Ç–æ–≤! –ú–∞–∫—Å–∏–º—É–º 20.');
        return;
      }
      setProducts([...products, normalized]);
      setProductInput('');
      setError('');
    }
  };

  const removeProduct = (product) => {
    setProducts(products.filter(p => p !== product));
  };

  const findRecipes = () => {
    if (products.length === 0) {
      setError('–î–æ–±–∞–≤—å—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω –ø—Ä–æ–¥—É–∫—Ç');
      return;
    }

    if (products.length > 15) {
      setError('–°–ø–∏—Å–æ–∫ –ø—Ä–æ–¥—É–∫—Ç–æ–≤ —Å–ª–∏—à–∫–æ–º –±–æ–ª—å—à–æ–π. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –∏—Å–ø–æ–ª—å–∑—É–π—Ç–µ –Ω–µ –±–æ–ª–µ–µ 15 –ø—Ä–æ–¥—É–∫—Ç–æ–≤.');
      setRecipes([]);
      return;
    }

    setLoading(true);
    setError('');

    // –ò–º–∏—Ç–∞—Ü–∏—è AI –ø–æ–∏—Å–∫–∞ (–≤ —Ä–µ–∞–ª—å–Ω–æ—Å—Ç–∏ –∑–¥–µ—Å—å –±—ã–ª –±—ã API –≤—ã–∑–æ–≤)
    setTimeout(() => {
      const foundRecipes = recipeDatabase.filter(recipe => {
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —Ö–æ—Ç—è –±—ã 2 –ø—Ä–æ–¥—É–∫—Ç–∞ –∏–∑ —Ä–µ—Ü–µ–ø—Ç–∞ –≤ —Å–ø–∏—Å–∫–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        const matchingIngredients = recipe.ingredients.filter(ingredient =>
          products.some(product => 
            ingredient.includes(product) || product.includes(ingredient)
          )
        );
        return matchingIngredients.length >= 2;
      });

      if (foundRecipes.length === 0) {
        setError('–†–µ—Ü–µ–ø—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å —Å–ø–∏—Å–æ–∫ –ø—Ä–æ–¥—É–∫—Ç–æ–≤.');
        setRecipes([]);
      } else {
        setRecipes(foundRecipes);
        setError('');
      }

      setLoading(false);
    }, 1500);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addProduct();
    }
  };

  return (
    <Container maxWidth="lg" style={{ padding: '32px 0' }}>
      <Paper elevation={3} style={{ padding: '32px', marginBottom: '32px' }}>
        <Typography variant="h4" component="h1" gutterBottom align="center" style={{ color: '#2E8B57' }}>
          ü§ñ AI –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ä–µ—Ü–µ–ø—Ç–æ–≤
        </Typography>
        <Typography variant="body1" color="text.secondary" align="center" style={{ marginBottom: '32px' }}>
          –í–≤–µ–¥–∏—Ç–µ —Å–ø–∏—Å–æ–∫ –ø—Ä–æ–¥—É–∫—Ç–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –µ—Å—Ç—å —É –≤–∞—Å –¥–æ–º–∞, –∏ –º—ã –ø–æ–¥–±–µ—Ä–µ–º –ø–æ–¥—Ö–æ–¥—è—â–∏–µ —Ä–µ—Ü–µ–ø—Ç—ã
        </Typography>

        {error && (
          <Alert severity={error.includes('–Ω–µ –Ω–∞–π–¥–µ–Ω') ? 'warning' : 'error'} style={{ marginBottom: '16px' }}>
            {error}
          </Alert>
        )}

        <Box style={{ marginBottom: '24px' }}>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={8}>
              <TextField
                fullWidth
                label="–î–æ–±–∞–≤–∏—Ç—å –ø—Ä–æ–¥—É–∫—Ç"
                value={productInput}
                onChange={(e) => setProductInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="–ù–∞–ø—Ä–∏–º–µ—Ä: —è–π—Ü–∞, –∫—É—Ä–∏—Ü–∞, –ø–æ–º–∏–¥–æ—Ä—ã..."
                helperText="–ù–∞–∂–º–∏—Ç–µ Enter –∏–ª–∏ –∫–Ω–æ–ø–∫—É –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è"
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <Button
                fullWidth
                variant="contained"
                onClick={addProduct}
                style={{ backgroundColor: '#2E8B57', height: '56px' }}
              >
                –î–æ–±–∞–≤–∏—Ç—å
              </Button>
            </Grid>
          </Grid>
        </Box>

        {products.length > 0 && (
          <Box style={{ marginBottom: '24px' }}>
            <Typography variant="h6" gutterBottom>
              –í–∞—à–∏ –ø—Ä–æ–¥—É–∫—Ç—ã ({products.length}/20):
            </Typography>
            <Box style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
              {products.map((product, index) => (
                <Chip
                  key={index}
                  label={product}
                  onDelete={() => removeProduct(product)}
                  deleteIcon={<Delete />}
                  style={{ backgroundColor: '#E8F5E9', color: '#2E8B57' }}
                />
              ))}
            </Box>
          </Box>
        )}

        <Box style={{ textAlign: 'center', marginBottom: '32px' }}>
          <Button
            variant="contained"
            size="large"
            onClick={findRecipes}
            disabled={loading || products.length === 0}
            startIcon={loading ? <CircularProgress size={20} /> : <Search />}
            style={{ 
              backgroundColor: '#2E8B57',
              padding: '12px 48px',
              fontSize: '1.1rem'
            }}
          >
            {loading ? '–ü–æ–∏—Å–∫ —Ä–µ—Ü–µ–ø—Ç–æ–≤...' : '–ü–æ–¥–æ–±—Ä–∞—Ç—å –±–ª—é–¥–∞'}
          </Button>
        </Box>

        {recipes.length > 0 && (
          <Box>
            <Typography variant="h5" gutterBottom style={{ color: '#2E8B57', marginBottom: '24px' }}>
              üçΩÔ∏è –ù–∞–π–¥–µ–Ω–Ω—ã–µ —Ä–µ—Ü–µ–ø—Ç—ã ({recipes.length})
            </Typography>
            <Grid container spacing={3}>
              {recipes.map((recipe, index) => (
                <Grid item xs={12} md={6} key={index}>
                  <Card style={{ height: '100%', borderLeft: '4px solid', borderColor: '#2E8B57' }}>
                    <CardContent>
                      <Box style={{ display: 'flex', alignItems: 'start', marginBottom: '16px' }}>
                        <Restaurant style={{ fontSize: 32, color: '#2E8B57', marginRight: '12px' }} />
                        <Box style={{ flex: 1 }}>
                          <Typography variant="h6" gutterBottom style={{ color: '#2E8B57' }}>
                            {recipe.name}
                          </Typography>
                          <Typography variant="body2" color="text.secondary" style={{ marginBottom: '12px' }}>
                            {recipe.description}
                          </Typography>
                        </Box>
                      </Box>

                      <Typography variant="subtitle2" gutterBottom>
                        –ò–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç—ã:
                      </Typography>
                      <Box style={{ display: 'flex', flexWrap: 'wrap', gap: '4px', marginBottom: '16px' }}>
                        {recipe.ingredients.map((ingredient, idx) => (
                          <Chip
                            key={idx}
                            label={ingredient}
                            size="small"
                            variant="outlined"
                            style={{ fontSize: '0.75rem' }}
                          />
                        ))}
                      </Box>

                      <Grid container spacing={2}>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#ED8936', fontWeight: 'bold' }}>
                              {recipe.calories}
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              –∫–∫–∞–ª
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#4299E1', fontWeight: 'bold' }}>
                              {recipe.protein}–≥
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              –ë–µ–ª–∫–∏
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#38A169', fontWeight: 'bold' }}>
                              {recipe.fat}–≥
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              –ñ–∏—Ä—ã
                            </Typography>
                          </Box>
                        </Grid>
                        <Grid item xs={3}>
                          <Box textAlign="center">
                            <Typography variant="h6" style={{ color: '#ED8936', fontWeight: 'bold' }}>
                              {recipe.carbs}–≥
                            </Typography>
                            <Typography variant="caption" color="text.secondary">
                              –£–≥–ª–µ–≤–æ–¥—ã
                            </Typography>
                          </Box>
                        </Grid>
                      </Grid>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}
      </Paper>
    </Container>
  );
};

export default RecipeGeneratorPage;
