import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  Grid,
  Alert,
  Container,
  Card,
  CardContent,
  Chip,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  IconButton
} from '@mui/material';
import {
  Search,
  Delete,
  Restaurant,
  Close,
  Visibility
} from '@mui/icons-material';
import { StorageService } from '../utils/storage';

const RecipeGeneratorPage = () => {
  const [productInput, setProductInput] = useState('');
  const [products, setProducts] = useState([]);
  const [recipes, setRecipes] = useState([]);
  const [recipeDatabase, setRecipeDatabase] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [dialogOpen, setDialogOpen] = useState(false);

  // Загружаем рецепты из хранилища при монтировании компонента
  useEffect(() => {
    const loadRecipes = async () => {
      const loadedRecipes = await StorageService.getRecipes();
      setRecipeDatabase(loadedRecipes);
    };
    loadRecipes();
  }, []);

  const normalizeProduct = (product) => {
    return product.toLowerCase().trim();
  };

  const addProduct = () => {
    const normalized = normalizeProduct(productInput);
    if (normalized && !products.includes(normalized)) {
      if (products.length >= 20) {
        setError('Слишком много продуктов! Максимум 20.');
        return;
      }
      setProducts([...products, normalized]);
      setProductInput('');
      setError('');
    }
  };

  const removeProduct = (product) => {
    setProducts(products.filter(p => p !== product));
  };

  // Словарь синонимов для улучшения поиска
  const getProductSynonyms = (product) => {
    const synonyms = {
      'яйца': ['яйцо', 'яичный', 'яичница', 'омлет'],
      'курица': ['куриное', 'куриный', 'куриная грудка', 'куриное филе', 'цыпленок'],
      'мясо': ['говядина', 'свинина', 'баранина', 'фарш'],
      'рыба': ['рыбный', 'рыбка', 'филе рыбы'],
      'помидоры': ['помидор', 'томат', 'томаты'],
      'огурцы': ['огурец'],
      'лук': ['луковица', 'репчатый лук'],
      'морковь': ['морковка'],
      'картофель': ['картошка', 'картофельный'],
      'капуста': ['капустка', 'белокочанная капуста'],
      'перец': ['болгарский перец', 'перчик'],
      'макароны': ['паста', 'макаронные изделия', 'спагетти'],
      'рис': ['рисовый'],
      'гречка': ['гречневая крупа', 'гречневая'],
      'творог': ['творожный'],
      'молоко': ['молочный'],
      'мука': ['мучной'],
      'масло': ['растительное масло', 'подсолнечное масло', 'оливковое масло'],
      'сыр': ['сырный'],
      'грибы': ['грибной', 'шампиньоны'],
      'кабачок': ['кабачки', 'цуккини'],
      'баклажан': ['баклажаны'],
      'брокколи': ['брокколи'],
      'лапша': ['вермишель', 'лапша'],
      'зелень': ['укроп', 'петрушка', 'базилик', 'зеленый лук']
    };

    const normalized = product.toLowerCase().trim();
    const productSynonyms = [normalized];

    // Ищем синонимы
    for (const [key, values] of Object.entries(synonyms)) {
      if (key === normalized || values.some(v => v === normalized)) {
        productSynonyms.push(key, ...values);
        break;
      }
    }

    return [...new Set(productSynonyms)];
  };

  const findRecipes = () => {
    if (products.length === 0) {
      setError('Добавьте хотя бы один продукт');
      return;
    }

    if (products.length > 15) {
      setError('Список продуктов слишком большой. Пожалуйста, используйте не более 15 продуктов.');
      setRecipes([]);
      return;
    }

    setLoading(true);
    setError('');

    // Имитация AI поиска (в реальности здесь был бы API вызов)
    setTimeout(() => {
      if (recipeDatabase.length === 0) {
        setError('База рецептов не загружена. Пожалуйста, обновите страницу.');
        setLoading(false);
        return;
      }

      // Улучшенный алгоритм поиска с синонимами и гибким сопоставлением
      const foundRecipes = recipeDatabase.map(recipe => {
        let matchScore = 0;
        let matchedIngredients = [];

        recipe.ingredients.forEach(ingredient => {
          const ingredientLower = ingredient.toLowerCase();

          products.forEach(product => {
            const productSynonyms = getProductSynonyms(product);

            // Проверяем точное совпадение или вхождение
            const isMatch = productSynonyms.some(synonym => {
              const synonymLower = synonym.toLowerCase();
              return ingredientLower.includes(synonymLower) || 
                     synonymLower.includes(ingredientLower) ||
                     ingredientLower === synonymLower;
            });

            if (isMatch) {
              matchScore++;
              if (!matchedIngredients.includes(ingredient)) {
                matchedIngredients.push(ingredient);
              }
            }
          });
        });

        return {
          ...recipe,
          matchScore,
          matchedIngredients,
          matchPercentage: (matchScore / recipe.ingredients.length) * 100
        };
      })
      .filter(recipe => recipe.matchScore > 0) // Находим рецепты хотя бы с 1 совпадением
      .sort((a, b) => {
        // Сортируем по количеству совпадений (больше = лучше)
        if (b.matchScore !== a.matchScore) {
          return b.matchScore - a.matchScore;
        }
        // Если совпадений одинаково, сортируем по проценту совпадения
        return b.matchPercentage - a.matchPercentage;
      });

      if (foundRecipes.length === 0) {
        setError('Рецепты не найдены. Попробуйте изменить список продуктов.');
        setRecipes([]);
      } else {
        // Убираем служебные поля перед отображением
        const recipesToShow = foundRecipes.map(({ matchScore, matchedIngredients, matchPercentage, ...recipe }) => recipe);
        setRecipes(recipesToShow);
        setError('');
      }

      setLoading(false);
    }, 1500);
  };

