import React, { useState, useEffect } from "react";
import {
  Paper,
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Tabs,
  Tab,
  CircularProgress,
  Snackbar,
  Alert,
} from "@mui/material";
import { Plus } from "lucide-react";

const API_BASE_URL = "http://localhost:8080/api";

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è —Ç–∞–±–æ–≤
function TabPanel({ children, value, index }) {
  return value === index ? <div>{children}</div> : null;
}

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "success",
  });

  // –ó–∞–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö –¥–∞–Ω–Ω—ã—Ö
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    try {
      await Promise.all([loadUsers(), loadDietPlans(), loadDiseases()]);
    } catch (e) {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö", "error");
    } finally {
      setLoading(false);
    }
  };

  const showSnackbar = (message, severity = "success") => {
    setSnackbar({ open: true, message, severity });
  };

  const loadUsers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/users`);
      const data = await res.json();
      setUsers(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π", "error");
    }
  };

  const loadDietPlans = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diet-plans`);
      const data = await res.json();
      setDietPlans(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤", "error");
    }
  };

  const loadDiseases = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diseases`);
      const data = await res.json();
      setDiseases(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π", "error");
    }
  };

  const deleteUser = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è?")) return;
    try {
      await fetch(`${API_BASE_URL}/users/${id}`, { method: "DELETE" });
      showSnackbar("–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —É–¥–∞–ª—ë–Ω");
      loadUsers();
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è", "error");
    }
  };

  const deleteDisease = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ?")) return;
    try {
      await fetch(`${API_BASE_URL}/diseases/${id}`, { method: "DELETE" });
      showSnackbar("–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ —É–¥–∞–ª–µ–Ω–æ");
      loadDiseases();
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è", "error");
    }
  };

  const deletePlan = async (id) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å —Ä–∞—Ü–∏–æ–Ω?")) return;
    try {
      await fetch(`${API_BASE_URL}/diet-plans/${id}`, { method: "DELETE" });
      showSnackbar("–†–∞—Ü–∏–æ–Ω —É–¥–∞–ª—ë–Ω");
      loadDietPlans();
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Ä–∞—Ü–∏–æ–Ω–∞", "error");
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        üõ†Ô∏è –ü–∞–Ω–µ–ª—å –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={(e, newValue) => setTabValue(newValue)}
          variant="fullWidth"
        >
          <Tab label="–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏" />
          <Tab label="–†–∞—Ü–∏–æ–Ω—ã" />
          <Tab label="–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è" />
        </Tabs>
      </Paper>

      {loading ? (
        <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          {/* –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ */}
          <TabPanel value={tabValue} index={0}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6" gutterBottom>
                –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏
              </Typography>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ò–º—è</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.length > 0 ? (
                    users.map((u) => (
                      <TableRow key={u.id}>
                        <TableCell>{u.id}</TableCell>
                        <TableCell>{u.username}</TableCell>
                        <TableCell>{u.email}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            size="small"
                            color="primary"
                            sx={{ mr: 1 }}
                            onClick={() =>
                              alert(`–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: ${u.username}`)
                            }
                          >
                            –ò–∑–º–µ–Ω–∏—Ç—å
                          </Button>
                          <Button
                            variant="outlined"
                            size="small"
                            color="error"
                            onClick={() => deleteUser(u.id)}
                          >
                            –£–¥–∞–ª–∏—Ç—å
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={4} align="center">
                        –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* –†–∞—Ü–∏–æ–Ω—ã */}
          <TabPanel value={tabValue} index={1}>
            <Paper sx={{ p: 2 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography variant="h6">–†–∞—Ü–∏–æ–Ω—ã</Typography>
                <Button
                  variant="contained"
                  color="success"
                  startIcon={<Plus size={18} />}
                  onClick={() => alert("–°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–≥–æ —Ä–∞—Ü–∏–æ–Ω–∞")}
                >
                  –°–æ–∑–¥–∞—Ç—å
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ö–∞–ª–æ—Ä–∏–∏</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {dietPlans.length > 0 ? (
                    dietPlans.map((plan) => (
                      <TableRow key={plan.id}>
                        <TableCell>{plan.id}</TableCell>
                        <TableCell>{plan.name}</TableCell>
                        <TableCell>{plan.disease?.name || "-"}</TableCell>
                        <TableCell>{plan.calories}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            color="primary"
                            size="small"
                            sx={{ mr: 1 }}
                            onClick={() =>
                              alert(`–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–∞—Ü–∏–æ–Ω–∞: ${plan.name}`)
                            }
                          >
                            –ò–∑–º–µ–Ω–∏—Ç—å
                          </Button>
                          <Button
                            variant="outlined"
                            color="error"
                            size="small"
                            onClick={() => deletePlan(plan.id)}
                          >
                            –£–¥–∞–ª–∏—Ç—å
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={5} align="center">
                        –ù–µ—Ç —Ä–∞—Ü–∏–æ–Ω–æ–≤
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* –ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è */}
          <TabPanel value={tabValue} index={2}>
            <Paper sx={{ p: 2 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography variant="h6">–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è</Typography>
                <Button
                  variant="contained"
                  color="success"
                  startIcon={<Plus size={18} />}
                  onClick={() => alert("–°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–≥–æ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è")}
                >
                  –°–æ–∑–¥–∞—Ç—å
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–û–ø–∏—Å–∞–Ω–∏–µ</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {diseases.length > 0 ? (
                    diseases.map((d) => (
                      <TableRow key={d.id}>
                        <TableCell>{d.id}</TableCell>
                        <TableCell>{d.name}</TableCell>
                        <TableCell>{d.description}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            color="primary"
                            size="small"
                            sx={{ mr: 1 }}
                            onClick={() =>
                              alert(`–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è: ${d.name}`)
                            }
                          >
                            –ò–∑–º–µ–Ω–∏—Ç—å
                          </Button>
                          <Button
                            variant="outlined"
                            color="error"
                            size="small"
                            onClick={() => deleteDisease(d.id)}
                          >
                            –£–¥–∞–ª–∏—Ç—å
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={4} align="center">
                        –ù–µ—Ç –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>
        </>
      )}

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert
          severity={snackbar.severity}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          variant="filled"
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AdminPage;
