import React, { useState, useEffect } from 'react';
import { AlertCircle, Users, FileText, Activity, Plus, Trash2, Eye, RefreshCw } from 'lucide-react';

const API_BASE_URL = 'http://localhost:8080/api';

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && <div>{children}</div>}
    </div>
  );
}

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // Данные
  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  
  // Статистика
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalPlans: 0,
    totalDiseases: 0,
    popularDiseases: []
  });

  // Диалоги
  const [showDiseaseDialog, setShowDiseaseDialog] = useState(false);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [notification, setNotification] = useState({ show: false, message: '', type: 'success' });

  // Форма добавления заболевания
  const [newDisease, setNewDisease] = useState({
    name: '',
    description: '',
    protein: 0,
    fat: 0,
    carbs: 0,
    calories: 0,
    vitamins: '',
    recommendations: ''
  });

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    setError(null);
    try {
      await Promise.all([
        loadUsers(),
        loadDietPlans(),
        loadDiseases()
      ]);
    } catch (err) {
      setError('Ошибка загрузки данных: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/users`);
      if (!response.ok) throw new Error('Ошибка загрузки пользователей');
      const data = await response.json();
      setUsers(data);
      return data;
    } catch (err) {
      console.error('Ошибка загрузки пользователей:', err);
      showNotification('Ошибка загрузки пользователей', 'error');
      return [];
    }
  };

  const loadDietPlans = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/diet-plans`);
      if (!response.ok) throw new Error('Ошибка загрузки рационов');
      const data = await response.json();
      setDietPlans(data);
      calculateStats(data);
      return data;
    } catch (err) {
      console.error('Ошибка загрузки рационов:', err);
      showNotification('Ошибка загрузки рационов', 'error');
      return [];
    }
  };

  const loadDiseases = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/diseases`);
      if (!response.ok) throw new Error('Ошибка загрузки заболеваний');
      const data = await response.json();
      setDiseases(data);
      return data;
    } catch (err) {
      console.error('Ошибка загрузки заболеваний:', err);
      showNotification('Ошибка загрузки заболеваний', 'error');
      return [];
    }
  };

  const calculateStats = (plans) => {
    const diseaseCounts = {};
    plans.forEach(plan => {
      if (plan.disease) {
        const diseaseName = plan.disease.name || plan.disease;
        diseaseCounts[diseaseName] = (diseaseCounts[diseaseName] || 0) + 1;
      }
    });

    const popularDiseases = Object.entries(diseaseCounts)
      .map(([disease, count]) => ({ disease, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    setStats({
      totalUsers: users.length,
      totalPlans: plans.length,
      totalDiseases: diseases.length,
      popularDiseases
    });
  };

  const deleteUser = async (userId) => {
    if (!window.confirm('Вы уверены, что хотите удалить этого пользователя?')) return;
    
    try {
      const response = await fetch(`${API_BASE_URL}/users/${userId}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Ошибка удаления пользователя');
      showNotification('Пользователь удален', 'success');
      loadUsers();
    } catch (err) {
      showNotification('Ошибка удаления пользователя', 'error');
    }
  };

  const deletePlan = async (planId) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот рацион?')) return;
    
    try {
      const response = await fetch(`${API_BASE_URL}/diet-plans/${planId}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Ошибка удаления рациона');
      showNotification('Рацион удален', 'success');
      loadDietPlans();
    } catch (err) {
      showNotification('Ошибка удаления рациона', 'error');
    }
  };

  const deleteDisease = async (diseaseId) => {
    if (!window.confirm('Вы уверены, что хотите удалить это заболевание?')) return;
    
    try {
      const response = await fetch(`${API_BASE_URL}/diseases/${diseaseId}`, {
        method: 'DELETE'
      });
      if (!response.ok) throw new Error('Ошибка удаления заболевания');
      showNotification('Заболевание удалено', 'success');
      loadDiseases();
    } catch (err) {
      showNotification('Ошибка удаления заболевания', 'error');
    }
  };

  const addDisease = async () => {
    if (!newDisease.name || !newDisease.description) {
      showNotification('Заполните обязательные поля', 'error');
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/diseases`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newDisease)
      });
      
      if (!response.ok) throw new Error('Ошибка добавления заболевания');
      
      showNotification('Заболевание добавлено', 'success');
      setShowDiseaseDialog(false);
      setNewDisease({
        name: '',
        description: '',
        protein: 0,
        fat: 0,
        carbs: 0,
        calories: 0,
        vitamins: '',
        recommendations: ''
      });
      loadDiseases();
    } catch (err) {
      showNotification('Ошибка добавления заболевания', 'error');
    }
  };

  const viewPlan = (plan) => {
    setSelectedPlan(plan);
    setShowViewDialog(true);
  };

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
    setTimeout(() => setNotification({ show: false, message: '', type: 'success' }), 3000);
  };

  const tabs = [
    { icon: Activity, label: 'Статистика' },
    { icon: Users, label: 'Пользователи' },
    { icon: FileText, label: 'Рационы' },
    { icon: AlertCircle, label: 'Заболевания' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500 p-8">
      <div className="max-w-7xl mx-auto">
        {/* Заголовок */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-white mb-2">🛠️ Панель администратора</h1>
          <p className="text-white/80">Управление системой в реальном времени</p>
        </div>

        {/* Табы */}
        <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-2xl mb-6 p-1">
          <div className="flex gap-2">
            {tabs.map((tab, index) => {
              const Icon = tab.icon;
              return (
                <button
                  key={index}
                  onClick={() => setTabValue(index)}
                  className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all ${
                    tabValue === index
                      ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  <Icon size={20} />
                  <span>{tab.label}</span>
                </button>
              );
            })}
          </div>
        </div>

        {/* Индикатор загрузки */}
        {loading && (
          <div className="mb-6 bg-white/95 rounded-lg p-4">
            <div className="animate-pulse flex items-center gap-3">
              <RefreshCw className="animate-spin text-indigo-500" size={24} />
              <span className="text-gray-600">Загрузка данных...</span>
            </div>
          </div>
        )}

        {/* Ошибка */}
        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4 text-red-700">
            {error}
          </div>
        )}

        {/* Уведомления */}
        {notification.show && (
          <div className={`fixed top-4 right-4 p-4 rounded-lg shadow-lg ${
            notification.type === 'success' ? 'bg-green-500' : 'bg-red-500'
          } text-white z-50 animate-fade-in`}>
            {notification.message}
          </div>
        )}

        {/* Таб: Статистика */}
        <TabPanel value={tabValue} index={0}>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
            <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
              <div className="flex items-center gap-4">
                <Users className="text-indigo-500" size={40} />
                <div>
                  <p className="text-3xl font-bold">{users.length}</p>
                  <p className="text-gray-600 text-sm">Пользователей</p>
                </div>
              </div>
            </div>
            
            <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
              <div className="flex items-center gap-4">
                <FileText className="text-purple-500" size={40} />
                <div>
                  <p className="text-3xl font-bold">{dietPlans.length}</p>
                  <p className="text-gray-600 text-sm">Рационов</p>
                </div>
              </div>
            </div>
            
            <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
              <div className="flex items-center gap-4">
                <Activity className="text-pink-500" size={40} />
                <div>
                  <p className="text-3xl font-bold">{users.length}</p>
                  <p className="text-gray-600 text-sm">Активных</p>
                </div>
              </div>
            </div>
            
            <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
              <div className="flex items-center gap-4">
                <AlertCircle className="text-teal-500" size={40} />
                <div>
                  <p className="text-3xl font-bold">{diseases.length}</p>
                  <p className="text-gray-600 text-sm">Заболеваний</p>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
            <h3 className="text-xl font-bold mb-4">📊 Популярные заболевания</h3>
            {stats.popularDiseases.length > 0 ? (
              stats.popularDiseases.map((item) => (
                <div key={item.disease} className="mb-4">
                  <div className="flex justify-between mb-2">
                    <span className="text-gray-700">{item.disease}</span>
                    <span className="font-bold text-indigo-600">{item.count}</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div
                      className="bg-gradient-to-r from-indigo-500 to-purple-500 h-2 rounded-full transition-all"
                      style={{ width: `${(item.count / Math.max(...stats.popularDiseases.map(d => d.count))) * 100}%` }}
                    />
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500">Нет данных о популярных заболеваниях</p>
            )}
          </div>
        </TabPanel>

        {/* Таб: Пользователи */}
        <TabPanel value={tabValue} index={1}>
          <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">👥 Управление пользователями</h3>
              <button
                onClick={loadUsers}
                className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-teal-500 to-green-500 text-white rounded-lg hover:shadow-lg transition-all"
              >
                <RefreshCw size={18} />
                Обновить
              </button>
            </div>
            
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4">ID</th>
                    <th className="text-left py-3 px-4">Имя</th>
                    <th className="text-left py-3 px-4">Email</th>
                    <th className="text-left py-3 px-4">Дата регистрации</th>
                    <th className="text-left py-3 px-4">Действия</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.id} className="border-b hover:bg-gray-50 transition-colors">
                      <td className="py-3 px-4">{user.id}</td>
                      <td className="py-3 px-4 font-semibold">{user.name}</td>
                      <td className="py-3 px-4">{user.email}</td>
                      <td className="py-3 px-4">{user.createdAt ? new Date(user.createdAt).toLocaleDateString() : '-'}</td>
                      <td className="py-3 px-4">
                        <button
                          onClick={() => deleteUser(user.id)}
                          className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                        >
                          <Trash2 size={18} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {users.length === 0 && (
                <div className="text-center py-8 text-gray-500">
                  Нет пользователей
                </div>
              )}
            </div>
          </div>
        </TabPanel>

        {/* Таб: Рационы */}
        <TabPanel value={tabValue} index={2}>
          <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">📋 Управление рационами питания</h3>
              <button
                onClick={loadDietPlans}
                className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-orange-500 to-red-500 text-white rounded-lg hover:shadow-lg transition-all"
              >
                <RefreshCw size={18} />
                Обновить
              </button>
            </div>
            
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4">Название</th>
                    <th className="text-left py-3 px-4">Заболевание</th>
                    <th className="text-left py-3 px-4">Калории</th>
                    <th className="text-left py-3 px-4">БЖУ</th>
                    <th className="text-left py-3 px-4">Период</th>
                    <th className="text-left py-3 px-4">Действия</th>
                  </tr>
                </thead>
                <tbody>
                  {dietPlans.map((plan) => (
                    <tr key={plan.id} className="border-b hover:bg-gray-50 transition-colors">
                      <td className="py-3 px-4 font-semibold">{plan.name}</td>
                      <td className="py-3 px-4">
                        <span className="px-3 py-1 bg-indigo-100 text-indigo-700 rounded-full text-sm">
                          {plan.disease?.name || 'Не указано'}
                        </span>
                      </td>
                      <td className="py-3 px-4">{plan.totalCalories} ккал</td>
                      <td className="py-3 px-4 text-sm">
                        Б: {plan.protein}г Ж: {plan.fat}г У: {plan.carbs}г
                      </td>
                      <td className="py-3 px-4">{plan.duration || '-'}</td>
                      <td className="py-3 px-4">
                        <div className="flex gap-2">
                          <button
                            onClick={() => viewPlan(plan)}
                            className="p-2 text-blue-500 hover:bg-blue-50 rounded-lg transition-colors"
                          >
                            <Eye size={18} />
                          </button>
                          <button
                            onClick={() => deletePlan(plan.id)}
                            className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {dietPlans.length === 0 && (
                <div className="text-center py-8 text-gray-500">
                  Нет рационов питания
                </div>
              )}
            </div>
          </div>
        </TabPanel>

        {/* Таб: Заболевания */}
        <TabPanel value={tabValue} index={3}>
          <div className="bg-white/95 backdrop-blur-lg rounded-xl shadow-lg p-6">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold">🏥 Управление заболеваниями</h3>
              <button
                onClick={() => setShowDiseaseDialog(true)}
                className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-teal-500 to-green-500 text-white rounded-lg hover:shadow-lg transition-all"
              >
                <Plus size={18} />
                Добавить заболевание
              </button>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {diseases.map((disease) => (
                <div key={disease.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <h4 className="text-lg font-bold mb-2">🏥 {disease.name}</h4>
                      <p className="text-gray-600 text-sm mb-2">{disease.description}</p>
                      <div className="grid grid-cols-2 gap-2 text-sm">
                        <p><span className="font-semibold">Калории:</span> {disease.calories}</p>
                        <p><span className="font-semibold">Белки:</span> {disease.protein}г</p>
                        <p><span className="font-semibold">Жиры:</span> {disease.fat}г</p>
                        <p><span className="font-semibold">Углеводы:</span> {disease.carbs}г</p>
                      </div>
                      {disease.vitamins && (
                        <p className="text-sm mt-2"><span className="font-semibold">Витамины:</span> {disease.vitamins}</p>
                      )}
                    </div>
                    <button
                      onClick={() => deleteDisease(disease.id)}
                      className="p-2 text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                    >
                      <Trash2 size={18} />
                    </button>
                  </div>
                </div>
              ))}
            </div>
            {diseases.length === 0 && (
              <div className="text-center py-8 text-gray-500">
                Нет заболеваний в базе данных
              </div>
            )}
          </div>
        </TabPanel>

        {/* Диалог добавления заболевания */}
        {showDiseaseDialog && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6">
                <h3 className="text-2xl font-bold mb-4">Добавить новое заболевание</h3>
                
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium mb-2">Название *</label>
                    <input
                      type="text"
                      value={newDisease.name}
                      onChange={(e) => setNewDisease({...newDisease, name: e.target.value})}
                      className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      placeholder="Например: Гипертония"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">Описание *</label>
                    <textarea
                      value={newDisease.description}
                      onChange={(e) => setNewDisease({...newDisease, description: e.target.value})}
                      className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      rows={3}
                      placeholder="Краткое описание заболевания"
                    />
                  </div>

                  <div className="grid grid-cols-3 gap-4">
                    <div>
                      <label className="block text-sm font-medium mb-2">Белки (г)</label>
                      <input
                        type="number"
                        value={newDisease.protein}
                        onChange={(e) => setNewDisease({...newDisease, protein: parseInt(e.target.value) || 0})}
                        className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-2">Жиры (г)</label>
                      <input
                        type="number"
                        value={newDisease.fat}
                        onChange={(e) => setNewDisease({...newDisease, fat: parseInt(e.target.value) || 0})}
                        className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium mb-2">Углеводы (г)</label>
                      <input
                        type="number"
                        value={newDisease.carbs}
                        onChange={(e) => setNewDisease({...newDisease, carbs: parseInt(e.target.value) || 0})}
                        className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">Калории</label>
                    <input
                      type="number"
                      value={newDisease.calories}
                      onChange={(e) => setNewDisease({...newDisease, calories: parseInt(e.target.value) || 0})}
                      className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">Витамины</label>
                    <input
                      type="text"
                      value={newDisease.vitamins}
                      onChange={(e) => setNewDisease({...newDisease, vitamins: e.target.value})}
                      className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      placeholder="Например: A, E, B2, B6"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-2">Рекомендации</label>
                    <textarea
                      value={newDisease.recommendations}
                      onChange={(e) => setNewDisease({...newDisease, recommendations: e.target.value})}
                      className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-indigo-500"
                      rows={2}
                      placeholder="Дополнительные рекомендации"
                    />
                  </div>
                </div>

                <div className="flex gap-3 mt-6">
                  <button
                    onClick={() => setShowDiseaseDialog(false)}
                    className="flex-1 px-4 py-2 border rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    Отмена
                  </button>
                  <button
                    onClick={addDisease}
                    className="flex-1 px-4 py-2 bg-gradient-to-r from-teal-500 to-green-500 text-white rounded-lg hover:shadow-lg transition-all"
                  >
                    Добавить
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Диалог просмотра рациона */}
        {showViewDialog && selectedPlan && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-xl shadow-2xl max-w-3xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6">
                <h3 className="text-2xl font-bold mb-4">Просмотр рациона: {selectedPlan.name}</h3>
                
                <div className="space-y-3 mb-6">
                  <p><strong>Заболевание:</strong> {selectedPlan.disease?.name || 'Не указано'}</p>
                  <p><strong>Калории:</strong> {selectedPlan.totalCalories} ккал/день</p>
                  <p><strong>БЖУ:</strong> Белки: {selectedPlan.protein}г, Жиры: {selectedPlan.fat}г, Углеводы: {selectedPlan.carbs}г</p>
                  <p><strong>Период:</strong> {selectedPlan.duration || 'Не указан'}</p>
                  {selectedPlan.recommendations && (
                    <p><strong>Рекомендации:</strong> {selectedPlan.recommendations}</p>
                  )}
                  {selectedPlan.vitamins && (
                    <p><strong>Витамины:</strong> {selectedPlan.vitamins}</p>
                  )}
                </div>

                {selectedPlan.dailyMeals && selectedPlan.dailyMeals.length > 0 && (
                  <div>
                    <h4 className="text-lg font-bold mb-3">Пример питания на день:</h4>
                    {selectedPlan.dailyMeals[0]?.meals?.map((meal, index) => (
                      <div key={index} className="border rounded-lg p-4 mb-3 hover:shadow-md transition-shadow">
                        <p className="font-bold text-indigo-600">{meal.type}</p>
                        <p className="font-semibold">{meal.name}</p>
                        <p className="text-sm text-gray-600">{meal.description}</p>
                        <p className="text-sm mt-2">
                          {meal.calories} ккал (Б: {meal.protein}г, Ж: {meal.fat}г, У: {meal.carbs}г)
                        </p>
                      </div>
                    ))}
                  </div>
                )}

                <div className="flex justify-end mt-6">
                  <button
                    onClick={() => setShowViewDialog(false)}
                    className="px-6 py-2 bg-gradient-to-r from-indigo-500 to-purple-500 text-white rounded-lg hover:shadow-lg transition-all"
                  >
                    Закрыть
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
