import React, { useState, useEffect } from "react";
import {
  AlertCircle,
  Users,
  FileText,
  Activity,
  Plus,
  Trash2,
  Eye,
  RefreshCw,
} from "lucide-react";
import {
  Paper,
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
} from "@mui/material";

const API_BASE_URL = "http://localhost:8080/api";

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalPlans: 0,
    totalDiseases: 0,
    popularDiseases: [],
  });
  const [notification, setNotification] = useState({
    show: false,
    message: "",
    type: "success",
  });
  const [newDisease, setNewDisease] = useState({
    name: "",
    description: "",
    protein: 0,
    fat: 0,
    carbs: 0,
    calories: 0,
    vitamins: "",
    recommendations: "",
  });

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    try {
      await Promise.all([loadUsers(), loadDietPlans(), loadDiseases()]);
    } catch (err) {
      setError("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/users`);
      if (!response.ok) throw new Error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π");
      const data = await response.json();
      setUsers(data);
      return data;
    } catch (err) {
      showNotification("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π", "error");
      return [];
    }
  };

  const loadDietPlans = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/diet-plans`);
      if (!response.ok) throw new Error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤");
      const data = await response.json();
      setDietPlans(data);
      calculateStats(data);
      return data;
    } catch (err) {
      showNotification("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤", "error");
      return [];
    }
  };

  const loadDiseases = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/diseases`);
      if (!response.ok) throw new Error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π");
      const data = await response.json();
      setDiseases(data);
      return data;
    } catch (err) {
      showNotification("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π", "error");
      return [];
    }
  };

  const calculateStats = (plans) => {
    const diseaseCounts = {};
    plans.forEach((plan) => {
      if (plan.disease) {
        const diseaseName = plan.disease.name || plan.disease;
        diseaseCounts[diseaseName] = (diseaseCounts[diseaseName] || 0) + 1;
      }
    });

    const popularDiseases = Object.entries(diseaseCounts)
      .map(([disease, count]) => ({ disease, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 5);

    setStats({
      totalUsers: users.length,
      totalPlans: plans.length,
      totalDiseases: diseases.length,
      popularDiseases,
    });
  };

  const deleteUser = async (userId) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è?")) return;
    try {
      const response = await fetch(`${API_BASE_URL}/users/${userId}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error();
      showNotification("–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —É–¥–∞–ª—ë–Ω", "success");
      loadUsers();
    } catch {
      showNotification("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è", "error");
    }
  };

  const deletePlan = async (planId) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å —Ä–∞—Ü–∏–æ–Ω?")) return;
    try {
      const response = await fetch(`${API_BASE_URL}/diet-plans/${planId}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error();
      showNotification("–†–∞—Ü–∏–æ–Ω —É–¥–∞–ª—ë–Ω", "success");
      loadDietPlans();
    } catch {
      showNotification("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Ä–∞—Ü–∏–æ–Ω–∞", "error");
    }
  };

  const deleteDisease = async (diseaseId) => {
    if (!window.confirm("–£–¥–∞–ª–∏—Ç—å –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ?")) return;
    try {
      const response = await fetch(`${API_BASE_URL}/diseases/${diseaseId}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error();
      showNotification("–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ —É–¥–∞–ª–µ–Ω–æ", "success");
      loadDiseases();
    } catch {
      showNotification("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è", "error");
    }
  };

  const addDisease = async () => {
    if (!newDisease.name || !newDisease.description) {
      showNotification("–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è", "error");
      return;
    }
    try {
      const response = await fetch(`${API_BASE_URL}/diseases`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newDisease),
      });
      if (!response.ok) throw new Error();
      showNotification("–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ –¥–æ–±–∞–≤–ª–µ–Ω–æ", "success");
      setNewDisease({
        name: "",
        description: "",
        protein: 0,
        fat: 0,
        carbs: 0,
        calories: 0,
        vitamins: "",
        recommendations: "",
      });
      loadDiseases();
    } catch {
      showNotification("–û—à–∏–±–∫–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏—è", "error");
    }
  };

  const showNotification = (message, type) => {
    setNotification({ show: true, message, type });
    setTimeout(() => setNotification({ show: false, message: "", type }), 3000);
  };

  const tabs = [
    { icon: Activity, label: "–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞" },
    { icon: Users, label: "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏" },
    { icon: FileText, label: "–†–∞—Ü–∏–æ–Ω—ã" },
    { icon: AlertCircle, label: "–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-700 via-purple-700 to-pink-600 p-8 font-sans text-gray-800">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-4xl font-extrabold text-white mb-4">
          üõ†Ô∏è –ü–∞–Ω–µ–ª—å –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
        </h1>

        {/* –¢–∞–±—ã */}
        <div className="bg-white/90 backdrop-blur-md rounded-2xl shadow-xl mb-6 p-2 flex gap-2">
          {tabs.map((tab, index) => {
            const Icon = tab.icon;
            return (
              <button
                key={index}
                onClick={() => setTabValue(index)}
                className={`flex items-center gap-2 px-6 py-3 rounded-xl font-semibold transition-all ${
                  tabValue === index
                    ? "bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg scale-105"
                    : "text-gray-600 hover:bg-gray-100 hover:scale-105"
                }`}
              >
                <Icon size={20} />
                {tab.label}
              </button>
            );
          })}
        </div>

        {/* –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è / –∑–∞–≥—Ä—É–∑–∫–∞ */}
        {loading && (
          <div className="mb-6 bg-white/90 rounded-xl p-4 flex items-center gap-3 shadow-md">
            <RefreshCw className="animate-spin text-indigo-500" size={24} />
            <span className="text-gray-600 font-medium">
              –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö...
            </span>
          </div>
        )}

        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-xl p-4 text-red-700 shadow-sm">
            {error}
          </div>
        )}

        {notification.show && (
          <div
            className={`fixed top-4 right-4 p-4 rounded-xl shadow-lg text-white z-50 ${
              notification.type === "success" ? "bg-green-500" : "bg-red-500"
            }`}
          >
            {notification.message}
          </div>
        )}

        {/* –¢–∞–±–ª–∏—Ü–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π */}
        {tabValue === 1 && (
          <Paper sx={{ p: 2 }}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                mb: 2,
              }}
            >
              <Typography variant="h6">–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</Typography>
            </Box>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>ID</TableCell>
                  <TableCell>–ò–º—è</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {users.map((u) => (
                  <TableRow key={u.id}>
                    <TableCell>{u.id}</TableCell>
                    <TableCell>{u.username}</TableCell>
                    <TableCell>{u.email}</TableCell>
                    <TableCell align="right">
                      <Button
                        variant="outlined"
                        color="primary"
                        size="small"
                        sx={{ mr: 1 }}
                        onClick={() =>
                          alert(`–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: ${u.username}`)
                        }
                      >
                        –ò–∑–º–µ–Ω–∏—Ç—å
                      </Button>
                      <Button
                        variant="outlined"
                        color="error"
                        size="small"
                        onClick={() => deleteUser(u.id)}
                      >
                        –£–¥–∞–ª–∏—Ç—å
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        )}

        {/* –¢–∞–±–ª–∏—Ü–∞ —Ä–∞—Ü–∏–æ–Ω–æ–≤ */}
        {tabValue === 2 && (
          <Paper sx={{ p: 2 }}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                mb: 2,
              }}
            >
              <Typography variant="h6">–†–∞—Ü–∏–æ–Ω—ã</Typography>
              <Button
                variant="contained"
                color="success"
                startIcon={<Plus size={18} />}
                onClick={() => alert("–û—Ç–∫—Ä—ã—Ç—å —Ñ–æ—Ä–º—É —Å–æ–∑–¥–∞–Ω–∏—è —Ä–∞—Ü–∏–æ–Ω–∞")}
              >
                –°–æ–∑–¥–∞—Ç—å
              </Button>
            </Box>
            {dietPlans.length === 0 ? (
              <Typography color="text.secondary">
                –†–∞—Ü–∏–æ–Ω—ã –ø–æ–∫–∞ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç
              </Typography>
            ) : (
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ö–∞–ª–æ—Ä–∏–∏</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {dietPlans.map((plan) => (
                    <TableRow key={plan.id}>
                      <TableCell>{plan.id}</TableCell>
                      <TableCell>{plan.name}</TableCell>
                      <TableCell>{plan.disease?.name || "-"}</TableCell>
                      <TableCell>{plan.calories}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </Paper>
        )}

        {/* –¢–∞–±–ª–∏—Ü–∞ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π */}
        {tabValue === 3 && (
          <Paper sx={{ p: 2 }}>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                mb: 2,
              }}
            >
              <Typography variant="h6">–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è</Typography>
              <Button
                variant="contained"
                color="success"
                startIcon={<Plus size={18} />}
                onClick={addDisease}
              >
                –°–æ–∑–¥–∞—Ç—å
              </Button>
            </Box>
            {diseases.length === 0 ? (
              <Typography color="text.secondary">
                –ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è –ø–æ–∫–∞ –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç
              </Typography>
            ) : (
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–û–ø–∏—Å–∞–Ω–∏–µ</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {diseases.map((d) => (
                    <TableRow key={d.id}>
                      <TableCell>{d.id}</TableCell>
                      <TableCell>{d.name}</TableCell>
                      <TableCell>{d.description}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            )}
          </Paper>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
