  { value: 'diabetes', label: 'ðŸŽ Ð¡Ð°Ñ…Ð°Ñ€Ð½Ñ‹Ð¹ Ð´Ð¸Ð°Ð±ÐµÑ‚ 2 Ñ‚Ð¸Ð¿Ð°' },
