  { value: 'gout', label: 'ðŸŸ£ ÐŸÐ¾Ð´Ð°Ð³Ñ€Ð°' },
  { value: 'osteoporosis', label: 'ðŸ¦´ ÐžÑÑ‚ÐµÐ¾Ð¿Ð¾Ñ€Ð¾Ð·' },
