  { value: 'ibs', label: 'ðŸ’Š Ð¡Ð¸Ð½Ð´Ñ€Ð¾Ð¼ Ñ€Ð°Ð·Ð´Ñ€Ð°Ð¶ÐµÐ½Ð½Ð¾Ð³Ð¾ ÐºÐ¸ÑˆÐµÑ‡Ð½Ð¸ÐºÐ°' },
  { value: 'crohns', label: 'ðŸ”´ Ð‘Ð¾Ð»ÐµÐ·Ð½ÑŒ ÐšÑ€Ð¾Ð½Ð°' },
  { value: 'ulcer', label: 'ðŸŸ¥ Ð¯Ð·Ð²ÐµÐ½Ð½Ð°Ñ Ð±Ð¾Ð»ÐµÐ·Ð½ÑŒ Ð¶ÐµÐ»ÑƒÐ´ÐºÐ°' },
  { value: 'kidney_disease', label: 'ðŸ«˜ Ð—Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ñ Ð¿Ð¾Ñ‡ÐµÐº' },
  { value: 'heart_disease', label: 'â¤ï¸ Ð—Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ñ ÑÐµÑ€Ð´Ñ†Ð°' },
  { value: 'liver_disease', label: 'ðŸŸ¢ Ð—Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ñ Ð¿ÐµÑ‡ÐµÐ½Ð¸' },
  { value: 'asthma', label: 'ðŸŒ¬ï¸ Ð‘Ñ€Ð¾Ð½Ñ…Ð¸Ð°Ð»ÑŒÐ½Ð°Ñ Ð°ÑÑ‚Ð¼Ð°' },
  { value: 'copd', label: 'ðŸ« Ð¥ÐžÐ‘Ð›' },
  { value: 'migraine', label: 'ðŸ’¢ ÐœÐ¸Ð³Ñ€ÐµÐ½ÑŒ' },
  { value: 'epilepsy', label: 'âš¡ Ð­Ð¿Ð¸Ð»ÐµÐ¿ÑÐ¸Ñ' },
  { value: 'depression', label: 'ðŸ’™ Ð”ÐµÐ¿Ñ€ÐµÑÑÐ¸Ñ' },
  { value: 'anxiety', label: 'ðŸ˜° Ð¢Ñ€ÐµÐ²Ð¾Ð¶Ð½Ð¾Ðµ Ñ€Ð°ÑÑÑ‚Ñ€Ð¾Ð¹ÑÑ‚Ð²Ð¾' },
  { value: 'pcos', label: 'ðŸŒ¸ Ð¡Ð¸Ð½Ð´Ñ€Ð¾Ð¼ Ð¿Ð¾Ð»Ð¸ÐºÐ¸ÑÑ‚Ð¾Ð·Ð½Ñ‹Ñ… ÑÐ¸Ñ‡Ð½Ð¸ÐºÐ¾Ð²' },
  { value: 'endometriosis', label: 'ðŸŒº Ð­Ð½Ð´Ð¾Ð¼ÐµÑ‚Ñ€Ð¸Ð¾Ð·' },
  { value: 'pregnancy', label: 'ðŸ¤° Ð‘ÐµÑ€ÐµÐ¼ÐµÐ½Ð½Ð¾ÑÑ‚ÑŒ' },
  { value: 'menopause', label: 'ðŸŒ™ ÐœÐµÐ½Ð¾Ð¿Ð°ÑƒÐ·Ð°' },
  { value: 'adhd', label: 'ðŸ§  Ð¡Ð”Ð’Ð“' },
  { value: 'autism', label: 'ðŸŒˆ Ð Ð°ÑÑÑ‚Ñ€Ð¾Ð¹ÑÑ‚Ð²Ð¾ Ð°ÑƒÑ‚Ð¸ÑÑ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ð³Ð¾ ÑÐ¿ÐµÐºÑ‚Ñ€Ð°' },
  { value: 'fibromyalgia', label: 'ðŸ’œ Ð¤Ð¸Ð±Ñ€Ð¾Ð¼Ð¸Ð°Ð»Ð³Ð¸Ñ' },
  { value: 'none', label: 'ÐÐµÑ‚ Ð·Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ð¹' }
];

const goals = [
  { value: 'weight_loss', label: 'âš–ï¸ ÐŸÐ¾Ñ…ÑƒÐ´ÐµÐ½Ð¸Ðµ', icon: <TrendingDown /> },
  { value: 'weight_gain', label: 'ðŸ’ª ÐÐ°Ð±Ð¾Ñ€ Ð¼Ð°ÑÑÑ‹', icon: <TrendingUp /> },
  { value: 'maintain', label: 'ðŸ”„ ÐŸÐ¾Ð´Ð´ÐµÑ€Ð¶Ð°Ð½Ð¸Ðµ Ð²ÐµÑÐ°', icon: <FitnessCenter /> }
];

const ProfilePage = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: localStorage.getItem('userName') || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ',
    email: localStorage.getItem('userEmail') || '',
    age: localStorage.getItem('userAge') || '',
    weight: localStorage.getItem('userWeight') || '',
    height: localStorage.getItem('userHeight') || '',
    disease: localStorage.getItem('userDisease') || '',
    goal: localStorage.getItem('userGoal') || '',
    joinDate: 'Ð¡ÐµÐ³Ð¾Ð´Ð½Ñ',
    plansCreated: 0
  });
  const [recentPlans, setRecentPlans] = useState([]);
  const [favoritePlans, setFavoritePlans] = useState([]);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [avatarDialogOpen, setAvatarDialogOpen] = useState(false);
  const [planViewDialogOpen, setPlanViewDialogOpen] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    email: '',
    age: '',
    weight: '',
    height: '',
    disease: '',
    goal: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadUserData();
    loadUserPlans();
    loadFavoritePlans();
  }, []);

  const loadFavoritePlans = () => {
    const favorites = JSON.parse(localStorage.getItem('favoritePlans') || '[]');
    setFavoritePlans(favorites);
  };

  const toggleFavoritePlan = (planId) => {
    const favorites = JSON.parse(localStorage.getItem('favoritePlans') || '[]');
    const index = favorites.indexOf(planId);

    if (index > -1) {
      favorites.splice(index, 1);
    } else {
      favorites.push(planId);
    }

    localStorage.setItem('favoritePlans', JSON.stringify(favorites));
    setFavoritePlans(favorites);
  };

  const isPlanFavorite = (planId) => {
    return favoritePlans.includes(planId);
  };

  const handleOpenPlanView = async (planId) => {
    const plans = await StorageService.getPlans();
    const plan = plans.find(p => p.id === planId);
    if (plan) {
      setSelectedPlan(plan);
      setPlanViewDialogOpen(true);
    }
  };

  const loadUserData = async () => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      const users = await StorageService.getUsers();
      const user = users.find(u => u.id === userId);
      if (user) {
        const disease = user.disease || localStorage.getItem('userDisease') || '';
        const goal = user.goal || localStorage.getItem('userGoal') || '';

        setUserData({
          name: user.name,
          email: user.email,
          age: user.age || '',
          weight: user.weight || '',
          height: user.height || '',
          disease: disease,
          goal: goal,
          joinDate: user.createdAt ? new Date(user.createdAt).toLocaleDateString('ru-RU') : 'Ð¡ÐµÐ³Ð¾Ð´Ð½Ñ',
          plansCreated: 0
        });

        // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð² localStorage Ð´Ð»Ñ Ð±Ñ‹ÑÑ‚Ñ€Ð¾Ð³Ð¾ Ð´Ð¾ÑÑ‚ÑƒÐ¿Ð°
        if (disease) localStorage.setItem('userDisease', disease);
        if (goal) localStorage.setItem('userGoal', goal);
      }
    } else {
      // Ð•ÑÐ»Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½ Ð² Ð±Ð°Ð·Ðµ, Ð·Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¸Ð· localStorage
      const disease = localStorage.getItem('userDisease') || '';
      const goal = localStorage.getItem('userGoal') || '';
      setUserData(prev => ({
        ...prev,
        disease: disease,
        goal: goal
      }));
    }
  };

  const handleOpenEditDialog = () => {
    setEditForm({
      name: userData.name,
      email: userData.email,
      age: userData.age,
      weight: userData.weight,
      height: userData.height,
      disease: userData.disease || '',
      goal: userData.goal || ''
    });
    setEditDialogOpen(true);
    setError('');
    setSuccess('');
  };

  const handleCloseEditDialog = () => {
    setEditDialogOpen(false);
    setError('');
    setSuccess('');
  };

  const handleSaveProfile = async () => {
    // Ð’Ð°Ð»Ð¸Ð´Ð°Ñ†Ð¸Ñ
    if (!editForm.name.trim()) {
      setError('Ð˜Ð¼Ñ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð¾ Ð´Ð»Ñ Ð·Ð°Ð¿Ð¾Ð»Ð½ÐµÐ½Ð¸Ñ');
      return;
    }

    if (!editForm.email.trim()) {
      setError('Email Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÐµÐ½ Ð´Ð»Ñ Ð·Ð°Ð¿Ð¾Ð»Ð½ÐµÐ½Ð¸Ñ');
      return;
    }

    if (!editForm.disease) {
      setError('ÐžÑÐ½Ð¾Ð²Ð½Ð¾Ðµ Ð·Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ðµ Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð¾ Ð´Ð»Ñ ÑƒÐºÐ°Ð·Ð°Ð½Ð¸Ñ');
      return;
    }

    if (!editForm.goal) {
      setError('Ð¦ÐµÐ»ÑŒ (Ð¿Ð¾Ñ…ÑƒÐ´ÐµÐ½Ð¸Ðµ/Ð½Ð°Ð±Ð¾Ñ€ Ð¼Ð°ÑÑÑ‹) Ð¾Ð±ÑÐ·Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð° Ð´Ð»Ñ ÑƒÐºÐ°Ð·Ð°Ð½Ð¸Ñ');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const userId = localStorage.getItem('userId');

      if (userId) {
        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð² Ð±Ð°Ð·Ðµ
        const updatedUser = await StorageService.updateUser(userId, {
          name: editForm.name,
          email: editForm.email,
          age: editForm.age ? parseInt(editForm.age) : null,
          weight: editForm.weight ? parseFloat(editForm.weight) : null,
          height: editForm.height ? parseFloat(editForm.height) : null,
          disease: editForm.disease,
          goal: editForm.goal
        });

        if (updatedUser) {
          // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ localStorage
          localStorage.setItem('userName', editForm.name);
          localStorage.setItem('userEmail', editForm.email);
          localStorage.setItem('userAge', editForm.age);
          localStorage.setItem('userWeight', editForm.weight);
          localStorage.setItem('userHeight', editForm.height);
          localStorage.setItem('userDisease', editForm.disease);
          localStorage.setItem('userGoal', editForm.goal);

          // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ
          setUserData(prev => ({
            ...prev,
            name: editForm.name,
            email: editForm.email,
            age: editForm.age,
            weight: editForm.weight,
            height: editForm.height,
            disease: editForm.disease,
            goal: editForm.goal
          }));

          setSuccess('ÐŸÑ€Ð¾Ñ„Ð¸Ð»ÑŒ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½!');
          setTimeout(() => {
            handleCloseEditDialog();
          }, 1000);
        } else {
          setError('ÐÐµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¾Ð±Ð½Ð¾Ð²Ð¸Ñ‚ÑŒ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»ÑŒ');
        }
      } else {
        // Ð•ÑÐ»Ð¸ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð½ÐµÑ‚ Ð² Ð±Ð°Ð·Ðµ, ÑÐ¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ð² localStorage
        localStorage.setItem('userName', editForm.name);
        localStorage.setItem('userEmail', editForm.email);
        localStorage.setItem('userAge', editForm.age);
        localStorage.setItem('userWeight', editForm.weight);
        localStorage.setItem('userHeight', editForm.height);
        localStorage.setItem('userDisease', editForm.disease);
        localStorage.setItem('userGoal', editForm.goal);

        setUserData(prev => ({
          ...prev,
          name: editForm.name,
          email: editForm.email,
          age: editForm.age,
          weight: editForm.weight,
          height: editForm.height,
          disease: editForm.disease,
          goal: editForm.goal
        }));

        setSuccess('ÐŸÑ€Ð¾Ñ„Ð¸Ð»ÑŒ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½!');
        setTimeout(() => {
          handleCloseEditDialog();
        }, 1000);
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ñ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ:', error);
      setError('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ ÑÐ¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ð¸ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ');
    } finally {
      setLoading(false);
    }
