              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <CalendarToday />
                </ListItemIcon>
                <ListItemText primary="Ð”Ð°Ñ‚Ð° Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð°Ñ†Ð¸Ð¸" secondary={userData.joinDate} />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <LocalHospital />
                </ListItemIcon>
                <ListItemText 
                  primary="ÐžÑÐ½Ð¾Ð²Ð½Ð¾Ðµ Ð·Ð°Ð±Ð¾Ð»ÐµÐ²Ð°Ð½Ð¸Ðµ" 
                  secondary={userData.disease ? diseases.find(d => d.value === userData.disease)?.label || userData.disease : 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾'} 
                />
              </ListItem>
              <ListItem>
                <ListItemIcon>
                  <FitnessCenter />
                </ListItemIcon>
                <ListItemText 
                  primary="Ð¦ÐµÐ»ÑŒ" 
                  secondary={userData.goal ? goals.find(g => g.value === userData.goal)?.label || userData.goal : 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½Ð¾'} 
                />
              </ListItem>
              {userData.age && (
                <ListItem>
                  <ListItemIcon>
                    <Person />
                  </ListItemIcon>
                  <ListItemText primary="Ð’Ð¾Ð·Ñ€Ð°ÑÑ‚" secondary={`${userData.age} Ð»ÐµÑ‚`} />
                </ListItem>
              )}
              {userData.weight && userData.height && (
                <ListItem>
                  <ListItemIcon>
                    <Person />
                  </ListItemIcon>
                  <ListItemText 
                    primary="ÐŸÐ°Ñ€Ð°Ð¼ÐµÑ‚Ñ€Ñ‹" 
                    secondary={`Ð’ÐµÑ: ${userData.weight} ÐºÐ³, Ð Ð¾ÑÑ‚: ${userData.height} ÑÐ¼`} 
                  />
                </ListItem>
              )}
            </List>

            <Button 
              variant="contained" 
              fullWidth 
              style={{ marginTop: '16px' }}
              startIcon={<Edit />}
              onClick={handleOpenEditDialog}
            >
              Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»ÑŒ
            </Button>
          </Paper>
        </Grid>

        <Grid item xs={12} md={8}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card style={{
                background: 'rgba(255, 255, 255, 0.85)',
                backdropFilter: 'blur(20px) saturate(180%)',
                WebkitBackdropFilter: 'blur(20px) saturate(180%)',
