import React, { useState, useEffect } from "react";
import {
  Paper,
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Tabs,
  Tab,
  CircularProgress,
  Snackbar,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
} from "@mui/material";
import { Plus } from "lucide-react";

const API_BASE_URL = "http://localhost:8080/api";

// Компонент для табов
function TabPanel({ children, value, index }) {
  return value === index ? <div>{children}</div> : null;
}

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "success",
  });

  // Модальные окна
  const [userDialog, setUserDialog] = useState({ 
    open: false, 
    mode: 'create', 
    data: { name: '', email: '', password: '' } 
  });
  const [planDialog, setPlanDialog] = useState({ 
    open: false, 
    mode: 'create', 
    data: { name: '', diseaseId: '', calories: '' } 
  });
  const [diseaseDialog, setDiseaseDialog] = useState({ 
    open: false, 
    mode: 'create', 
    data: { name: '', description: '' } 
  });

  // Загрузка всех данных
  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    setLoading(true);
    try {
      console.log('Loading data from:', API_BASE_URL);
      await Promise.all([loadUsers(), loadDietPlans(), loadDiseases()]);
    } catch (e) {
      console.error('Error in loadAllData:', e);
      showSnackbar("Ошибка загрузки данных. Проверьте консоль и CORS настройки.", "error");
    } finally {
      setLoading(false);
    }
  };

  const showSnackbar = (message, severity = "success") => {
    setSnackbar({ open: true, message, severity });
  };

  const loadUsers = async () => {
    try {
      console.log('Fetching users from:', `${API_BASE_URL}/users`);
      const res = await fetch(`${API_BASE_URL}/users`);
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      const data = await res.json();
      console.log('Users loaded:', data);
      setUsers(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error loading users:', error);
      showSnackbar(`Ошибка загрузки пользователей: ${error.message}`, "error");
    }
  };

  const loadDietPlans = async () => {
    try {
      console.log('Fetching diet plans from:', `${API_BASE_URL}/diet-plans`);
      const res = await fetch(`${API_BASE_URL}/diet-plans`);
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      const data = await res.json();
      console.log('Diet plans loaded:', data);
      setDietPlans(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error loading diet plans:', error);
      showSnackbar(`Ошибка загрузки рационов: ${error.message}`, "error");
    }
  };

  const loadDiseases = async () => {
    try {
      console.log('Fetching diseases from:', `${API_BASE_URL}/diseases`);
      const res = await fetch(`${API_BASE_URL}/diseases`);
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      const data = await res.json();
      console.log('Diseases loaded:', data);
      setDiseases(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error loading diseases:', error);
      showSnackbar(`Ошибка загрузки заболеваний: ${error.message}`, "error");
    }
  };

  const deleteUser = async (id) => {
    if (!window.confirm("Удалить пользователя?")) return;
    try {
      const res = await fetch(`${API_BASE_URL}/users/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      showSnackbar("Пользователь удалён");
      loadUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
      showSnackbar("Ошибка удаления пользователя", "error");
    }
  };

  const deleteDisease = async (id) => {
    if (!window.confirm("Удалить заболевание?")) return;
    try {
      const res = await fetch(`${API_BASE_URL}/diseases/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      showSnackbar("Заболевание удалено");
      loadDiseases();
    } catch (error) {
      console.error('Error deleting disease:', error);
      showSnackbar("Ошибка удаления заболевания", "error");
    }
  };

  const deletePlan = async (id) => {
    if (!window.confirm("Удалить рацион?")) return;
    try {
      const res = await fetch(`${API_BASE_URL}/diet-plans/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      showSnackbar("Рацион удалён");
      loadDietPlans();
    } catch (error) {
      console.error('Error deleting diet plan:', error);
      showSnackbar("Ошибка удаления рациона", "error");
    }
  };

  // Создание и редактирование пользователей
  const handleUserSubmit = async () => {
    const { mode, data } = userDialog;
    
    if (!data.name || !data.email) {
      showSnackbar('Заполните все обязательные поля', 'error');
      return;
    }
    
    if (mode === 'create' && !data.password) {
      showSnackbar('Введите пароль', 'error');
      return;
    }
    
    try {
      const method = mode === 'create' ? 'POST' : 'PUT';
      const url = mode === 'create' 
        ? `${API_BASE_URL}/users` 
        : `${API_BASE_URL}/users/${data.id}`;
      
      console.log('Submitting user:', method, url, data);
      
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      
      if (!res.ok) {
        const errorText = await res.text();
        console.error('Server error:', errorText);
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      
      showSnackbar(mode === 'create' ? 'Пользователь создан' : 'Пользователь обновлён');
      setUserDialog({ open: false, mode: 'create', data: { name: '', email: '', password: '' } });
      loadUsers();
    } catch (error) {
      console.error('Error saving user:', error);
      showSnackbar('Ошибка сохранения пользователя', 'error');
    }
  };

  // Создание и редактирование рационов
  const handlePlanSubmit = async () => {
    const { mode, data } = planDialog;
    
    if (!data.name || !data.calories) {
      showSnackbar('Заполните все обязательные поля', 'error');
      return;
    }
    
    try {
      const method = mode === 'create' ? 'POST' : 'PUT';
      const url = mode === 'create' 
        ? `${API_BASE_URL}/diet-plans` 
        : `${API_BASE_URL}/diet-plans/${data.id}`;
      
      console.log('Submitting plan:', method, url, data);
      
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });
      
      if (!res.ok) {
        const errorText = await res.text();
        console.error('Server error:', errorText);
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      
      showSnackbar(mode === 'create' ? 'Рацион создан' : 'Рацион обновлён');
      setPlanDialog({ open: false, mode: 'create', data: { name: '', diseaseId: '', calories: '' } });
      loadDietPlans();
    } catch (error) {
      console.error('Error saving plan:', error);
      showSnackbar('Ошибка сохранения рациона', 'error');
    }
  };

const handleDiseaseSubmit = async () => {
  const { mode, data } = diseaseDialog;

  if (!data.name) {
    showSnackbar('Введите название заболевания', 'error');
    return;
  }

  try {
    const method = mode === 'create' ? 'POST' : 'PUT';
    const url = mode === 'create'
      ? `${API_BASE_URL}/diseases`
      : `${API_BASE_URL}/diseases/${data.id}`;

    // ⚠️ Убираем id при создании новой болезни
    const requestData = { ...data };
    if (mode === 'create') delete requestData.id;

    console.log('Submitting disease:', method, url, requestData);

    const res = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(requestData),
    });

    if (!res.ok) {
      const errorText = await res.text();
      console.error('Server error:', errorText);
      throw new Error(`HTTP error! status: ${res.status}`);
    }

    showSnackbar(mode === 'create' ? 'Заболевание создано' : 'Заболевание обновлено');
    setDiseaseDialog({ open: false, mode: 'create', data: { name: '', description: '' } });
    loadDiseases();
  } catch (error) {
    console.error('Error saving disease:', error);
    showSnackbar('Ошибка сохранения заболевания', 'error');
  }
};


  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        🛠️ Панель администратора
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs
          value={tabValue}
          onChange={(e, newValue) => setTabValue(newValue)}
          variant="fullWidth"
        >
          <Tab label="Пользователи" />
          <Tab label="Рационы" />
          <Tab label="Заболевания" />
        </Tabs>
      </Paper>

      {loading ? (
        <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          {/* Пользователи */}
          <TabPanel value={tabValue} index={0}>
            <Paper sx={{ p: 2 }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center", mb: 2 }}>
                <Typography variant="h6">Пользователи</Typography>
                <Button
                  variant="contained"
                  color="success"
                  startIcon={<Plus size={18} />}
                  onClick={() => setUserDialog({ 
                    open: true, 
                    mode: 'create', 
                    data: { name: '', email: '', password: '' } 
                  })}
                >
                  Создать
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Имя</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell align="right">Действия</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.length > 0 ? (
                    users.map((u) => (
                      <TableRow key={u.id}>
                        <TableCell>{u.id}</TableCell>
                        <TableCell>{u.name}</TableCell>
                        <TableCell>{u.email}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            size="small"
                            color="primary"
                            sx={{ mr: 1 }}
                            onClick={() => setUserDialog({ 
                              open: true, 
                              mode: 'edit', 
                              data: { id: u.id, name: u.name, email: u.email } 
                            })}
                          >
                            Изменить
                          </Button>
                          <Button
                            variant="outlined"
                            size="small"
                            color="error"
                            onClick={() => deleteUser(u.id)}
                          >
                            Удалить
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={4} align="center">
                        Нет данных
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* Рационы */}
          <TabPanel value={tabValue} index={1}>
            <Paper sx={{ p: 2 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography variant="h6">Рационы</Typography>
                <Button
                  variant="contained"
                  color="success"
                  startIcon={<Plus size={18} />}
                  onClick={() => setPlanDialog({ 
                    open: true, 
                    mode: 'create', 
                    data: { name: '', diseaseId: '', calories: '' } 
                  })}
                >
                  Создать
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Название</TableCell>
                    <TableCell>Заболевание</TableCell>
                    <TableCell>Калории</TableCell>
                    <TableCell align="right">Действия</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {dietPlans.length > 0 ? (
                    dietPlans.map((plan) => (
                      <TableRow key={plan.id}>
                        <TableCell>{plan.id}</TableCell>
                        <TableCell>{plan.name}</TableCell>
                        <TableCell>{plan.disease?.name || "-"}</TableCell>
                        <TableCell>{plan.calories}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            color="primary"
                            size="small"
                            sx={{ mr: 1 }}
                            onClick={() => setPlanDialog({ 
                              open: true, 
                              mode: 'edit', 
                              data: { 
                                id: plan.id,
                                name: plan.name,
                                diseaseId: plan.disease?.id || '',
                                calories: plan.calories
                              } 
                            })}
                          >
                            Изменить
                          </Button>
                          <Button
                            variant="outlined"
                            color="error"
                            size="small"
                            onClick={() => deletePlan(plan.id)}
                          >
                            Удалить
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={5} align="center">
                        Нет рационов
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* Заболевания */}
          <TabPanel value={tabValue} index={2}>
            <Paper sx={{ p: 2 }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  mb: 2,
                }}
              >
                <Typography variant="h6">Заболевания</Typography>
                <Button
                  variant="contained"
                  color="success"
                  startIcon={<Plus size={18} />}
                  onClick={() => setDiseaseDialog({ 
                    open: true, 
                    mode: 'create', 
                    data: { name: '', description: '' } 
                  })}
                >
                  Создать
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Название</TableCell>
                    <TableCell>Описание</TableCell>
                    <TableCell align="right">Действия</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {diseases.length > 0 ? (
                    diseases.map((d) => (
                      <TableRow key={d.id}>
                        <TableCell>{d.id}</TableCell>
                        <TableCell>{d.name}</TableCell>
                        <TableCell>{d.description}</TableCell>
                        <TableCell align="right">
                          <Button
                            variant="outlined"
                            color="primary"
                            size="small"
                            sx={{ mr: 1 }}
                            onClick={() => setDiseaseDialog({ 
                              open: true, 
                              mode: 'edit', 
                              data: { id: d.id, name: d.name, description: d.description } 
                            })}
                          >
                            Изменить
                          </Button>
                          <Button
                            variant="outlined"
                            color="error"
                            size="small"
                            onClick={() => deleteDisease(d.id)}
                          >
                            Удалить
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={4} align="center">
                        Нет заболеваний
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>
        </>
      )}

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert
          severity={snackbar.severity}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          variant="filled"
        >
          {snackbar.message}
        </Alert>
      </Snackbar>

      {/* Диалог для пользователей */}
      <Dialog 
        open={userDialog.open} 
        onClose={() => setUserDialog({ open: false, mode: 'create', data: { name: '', email: '', password: '' } })}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {userDialog.mode === 'create' ? 'Создать пользователя' : 'Редактировать пользователя'}
        </DialogTitle>
        <DialogContent>
          <TextField
            margin="normal"
            label="Имя пользователя"
            fullWidth
            required
            value={userDialog.data.name || ''}
            onChange={(e) => setUserDialog({
              ...userDialog,
              data: { ...userDialog.data, name: e.target.value }
            })}
          />
          <TextField
            margin="normal"
            label="Email"
            type="email"
            fullWidth
            required
            value={userDialog.data.email || ''}
            onChange={(e) => setUserDialog({
              ...userDialog,
              data: { ...userDialog.data, email: e.target.value }
            })}
          />
          {userDialog.mode === 'create' && (
            <TextField
              margin="normal"
              label="Пароль"
              type="password"
              fullWidth
              required
              value={userDialog.data.password || ''}
              onChange={(e) => setUserDialog({
                ...userDialog,
                data: { ...userDialog.data, password: e.target.value }
              })}
            />
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setUserDialog({ open: false, mode: 'create', data: { name: '', email: '', password: '' } })}>
            Отмена
          </Button>
          <Button onClick={handleUserSubmit} variant="contained">
            {userDialog.mode === 'create' ? 'Создать' : 'Сохранить'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Диалог для рационов */}
      <Dialog 
        open={planDialog.open} 
        onClose={() => setPlanDialog({ open: false, mode: 'create', data: { name: '', diseaseId: '', calories: '' } })}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {planDialog.mode === 'create' ? 'Создать рацион' : 'Редактировать рацион'}
        </DialogTitle>
        <DialogContent>
          <TextField
            margin="normal"
            label="Название рациона"
            fullWidth
            required
            value={planDialog.data.name || ''}
            onChange={(e) => setPlanDialog({
              ...planDialog,
              data: { ...planDialog.data, name: e.target.value }
            })}
          />
          <TextField
            margin="normal"
            label="Заболевание"
            select
            fullWidth
            value={planDialog.data.diseaseId || ''}
            onChange={(e) => setPlanDialog({
              ...planDialog,
              data: { ...planDialog.data, diseaseId: e.target.value }
            })}
          >
            <MenuItem value="">Не выбрано</MenuItem>
            {diseases.map((d) => (
              <MenuItem key={d.id} value={d.id}>
                {d.name}
              </MenuItem>
            ))}
          </TextField>
          <TextField
            margin="normal"
            label="Калории"
            type="number"
            fullWidth
            required
            value={planDialog.data.calories || ''}
            onChange={(e) => setPlanDialog({
              ...planDialog,
              data: { ...planDialog.data, calories: e.target.value }
            })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setPlanDialog({ open: false, mode: 'create', data: { name: '', diseaseId: '', calories: '' } })}>
            Отмена
          </Button>
          <Button onClick={handlePlanSubmit} variant="contained">
            {planDialog.mode === 'create' ? 'Создать' : 'Сохранить'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Диалог для заболеваний */}
      <Dialog 
        open={diseaseDialog.open} 
        onClose={() => setDiseaseDialog({ open: false, mode: 'create', data: { name: '', description: '' } })}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {diseaseDialog.mode === 'create' ? 'Создать заболевание' : 'Редактировать заболевание'}
        </DialogTitle>
        <DialogContent>
          <TextField
            margin="normal"
            label="Название"
            fullWidth
            required
            value={diseaseDialog.data.name || ''}
            onChange={(e) => setDiseaseDialog({
              ...diseaseDialog,
              data: { ...diseaseDialog.data, name: e.target.value }
            })}
          />
          <TextField
            margin="normal"
            label="Описание"
            fullWidth
            multiline
            rows={4}
            value={diseaseDialog.data.description || ''}
            onChange={(e) => setDiseaseDialog({
              ...diseaseDialog,
              data: { ...diseaseDialog.data, description: e.target.value }
            })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDiseaseDialog({ open: false, mode: 'create', data: { name: '', description: '' } })}>
            Отмена
          </Button>
          <Button onClick={handleDiseaseSubmit} variant="contained">
            {diseaseDialog.mode === 'create' ? 'Создать' : 'Сохранить'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default AdminPage;
