import React, { useState, useEffect } from "react";
import {
  Paper,
  Box,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Tabs,
  Tab,
  CircularProgress,
  Snackbar,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import { Plus } from "lucide-react";

const API_BASE_URL = "http://localhost:8080/api";

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è —Ç–∞–±–æ–≤
function TabPanel({ children, value, index }) {
  return value === index ? <div>{children}</div> : null;
}

const AdminPage = () => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [users, setUsers] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [diseases, setDiseases] = useState([]);
  const [snackbar, setSnackbar] = useState({ open: false, message: "", severity: "success" });

  // –î–ª—è –º–æ–¥–∞–ª—å–Ω—ã—Ö –æ–∫–æ–Ω
  const [dialogOpen, setDialogOpen] = useState(false);
  const [dialogType, setDialogType] = useState(""); // "user" | "diet" | "disease"
  const [editItem, setEditItem] = useState(null); // –æ–±—ä–µ–∫—Ç –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è

  // –î–∞–Ω–Ω—ã–µ —Ñ–æ—Ä–º—ã
  const [formData, setFormData] = useState({});

  // –ó–∞–≥—Ä—É–∑–∫–∞ –≤—Å–µ—Ö –¥–∞–Ω–Ω—ã—Ö
  useEffect(() => {
    loadAllData();
  }, []);

  const showSnackbar = (message, severity = "success") => {
    setSnackbar({ open: true, message, severity });
  };

  const loadAllData = async () => {
    setLoading(true);
    try {
      await Promise.all([loadUsers(), loadDietPlans(), loadDiseases()]);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö", "error");
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/users`);
      const data = await res.json();
      setUsers(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π", "error");
    }
  };

  const loadDietPlans = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diet-plans`);
      const data = await res.json();
      setDietPlans(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Ä–∞—Ü–∏–æ–Ω–æ–≤", "error");
    }
  };

  const loadDiseases = async () => {
    try {
      const res = await fetch(`${API_BASE_URL}/diseases`);
      const data = await res.json();
      setDiseases(data);
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–π", "error");
    }
  };

  const deleteItem = async (type, id) => {
    if (!window.confirm("–í—ã —É–≤–µ—Ä–µ–Ω—ã?")) return;
    try {
      await fetch(`${API_BASE_URL}/${type}/${id}`, { method: "DELETE" });
      showSnackbar(`${type === "users" ? "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å" : type === "diet-plans" ? "–†–∞—Ü–∏–æ–Ω" : "–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ"} —É–¥–∞–ª—ë–Ω`);
      loadAllData();
    } catch {
      showSnackbar("–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è", "error");
    }
  };

  const openDialog = (type, item = null) => {
    setDialogType(type);
    setEditItem(item);
    if (item) {
      setFormData(item);
    } else {
      setFormData({});
    }
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
    setFormData({});
    setEditItem(null);
  };

  const handleFormSubmit = async () => {
    try {
      let url = `${API_BASE_URL}/${dialogType === "user" ? "users" : dialogType === "diet" ? "diet-plans" : "diseases"}`;
      let method = editItem ? "PUT" : "POST";
      if (editItem) url += `/${editItem.id}`;

      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!res.ok) throw new Error("–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è");

      showSnackbar(editItem ? "–û–±–Ω–æ–≤–ª–µ–Ω–æ —É—Å–ø–µ—à–Ω–æ" : "–°–æ–∑–¥–∞–Ω–æ —É—Å–ø–µ—à–Ω–æ");
      loadAllData();
      handleDialogClose();
    } catch (e) {
      showSnackbar(e.message, "error");
    }
  };

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        üõ†Ô∏è –ü–∞–Ω–µ–ª—å –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
      </Typography>

      <Paper sx={{ mb: 3 }}>
        <Tabs value={tabValue} onChange={(e, val) => setTabValue(val)} variant="fullWidth">
          <Tab label="–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏" />
          <Tab label="–†–∞—Ü–∏–æ–Ω—ã" />
          <Tab label="–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è" />
        </Tabs>
      </Paper>

      {loading ? (
        <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          {/* –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ */}
          <TabPanel value={tabValue} index={0}>
            <Paper sx={{ p: 2 }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", mb: 2 }}>
                <Typography variant="h6">–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</Typography>
                <Button variant="contained" color="success" startIcon={<Plus size={18} />} onClick={() => openDialog("user")}>
                  –î–æ–±–∞–≤–∏—Ç—å
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ò–º—è</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.length ? users.map((u) => (
                    <TableRow key={u.id}>
                      <TableCell>{u.id}</TableCell>
                      <TableCell>{u.username}</TableCell>
                      <TableCell>{u.email}</TableCell>
                      <TableCell align="right">
                        <Button variant="outlined" size="small" color="primary" sx={{ mr: 1 }} onClick={() => openDialog("user", u)}>
                          –ò–∑–º–µ–Ω–∏—Ç—å
                        </Button>
                        <Button variant="outlined" size="small" color="error" onClick={() => deleteItem("users", u.id)}>
                          –£–¥–∞–ª–∏—Ç—å
                        </Button>
                      </TableCell>
                    </TableRow>
                  )) : <TableRow><TableCell colSpan={4} align="center">–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö</TableCell></TableRow>}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* –†–∞—Ü–∏–æ–Ω—ã */}
          <TabPanel value={tabValue} index={1}>
            <Paper sx={{ p: 2 }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", mb: 2 }}>
                <Typography variant="h6">–†–∞—Ü–∏–æ–Ω—ã</Typography>
                <Button variant="contained" color="success" startIcon={<Plus size={18} />} onClick={() => openDialog("diet")}>
                  –î–æ–±–∞–≤–∏—Ç—å
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–ö–∞–ª–æ—Ä–∏–∏</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {dietPlans.length ? dietPlans.map((plan) => (
                    <TableRow key={plan.id}>
                      <TableCell>{plan.id}</TableCell>
                      <TableCell>{plan.name}</TableCell>
                      <TableCell>{plan.disease?.name || "-"}</TableCell>
                      <TableCell>{plan.calories}</TableCell>
                      <TableCell align="right">
                        <Button variant="outlined" size="small" color="primary" sx={{ mr: 1 }} onClick={() => openDialog("diet", plan)}>
                          –ò–∑–º–µ–Ω–∏—Ç—å
                        </Button>
                        <Button variant="outlined" size="small" color="error" onClick={() => deleteItem("diet-plans", plan.id)}>
                          –£–¥–∞–ª–∏—Ç—å
                        </Button>
                      </TableCell>
                    </TableRow>
                  )) : <TableRow><TableCell colSpan={5} align="center">–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö</TableCell></TableRow>}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>

          {/* –ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è */}
          <TabPanel value={tabValue} index={2}>
            <Paper sx={{ p: 2 }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", mb: 2 }}>
                <Typography variant="h6">–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏—è</Typography>
                <Button variant="contained" color="success" startIcon={<Plus size={18} />} onClick={() => openDialog("disease")}>
                  –î–æ–±–∞–≤–∏—Ç—å
                </Button>
              </Box>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>–ù–∞–∑–≤–∞–Ω–∏–µ</TableCell>
                    <TableCell>–û–ø–∏—Å–∞–Ω–∏–µ</TableCell>
                    <TableCell align="right">–î–µ–π—Å—Ç–≤–∏—è</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {diseases.length ? diseases.map((d) => (
                    <TableRow key={d.id}>
                      <TableCell>{d.id}</TableCell>
                      <TableCell>{d.name}</TableCell>
                      <TableCell>{d.description}</TableCell>
                      <TableCell align="right">
                        <Button variant="outlined" size="small" color="primary" sx={{ mr: 1 }} onClick={() => openDialog("disease", d)}>
                          –ò–∑–º–µ–Ω–∏—Ç—å
                        </Button>
                        <Button variant="outlined" size="small" color="error" onClick={() => deleteItem("diseases", d.id)}>
                          –£–¥–∞–ª–∏—Ç—å
                        </Button>
                      </TableCell>
                    </TableRow>
                  )) : <TableRow><TableCell colSpan={4} align="center">–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö</TableCell></TableRow>}
                </TableBody>
              </Table>
            </Paper>
          </TabPanel>
        </>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è/—Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      <Dialog open={dialogOpen} onClose={handleDialogClose} maxWidth="sm" fullWidth>
        <DialogTitle>{editItem ? "–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å" : "–°–æ–∑–¥–∞—Ç—å"} {dialogType === "user" ? "–ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è" : dialogType === "diet" ? "—Ä–∞—Ü–∏–æ–Ω" : "–∑–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ"}</DialogTitle>
        <DialogContent>
          {dialogType === "user" && (
            <>
              <TextField
                fullWidth
                label="–ò–º—è"
                margin="normal"
                value={formData.username || ""}
                onChange={(e) => setFormData({ ...formData, username: e.target.value })}
              />
              <TextField
                fullWidth
                label="Email"
                margin="normal"
                value={formData.email || ""}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
            </>
          )}
          {dialogType === "diet" && (
            <>
              <TextField
                fullWidth
                label="–ù–∞–∑–≤–∞–Ω–∏–µ"
                margin="normal"
                value={formData.name || ""}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              <FormControl fullWidth margin="normal">
                <InputLabel>–ó–∞–±–æ–ª–µ–≤–∞–Ω–∏–µ</InputLabel>
                <Select
                  value={formData.disease?.id || ""}
                  onChange={(e) => setFormData({ ...formData, disease: { id: e.target.value } })}
                >
                  {diseases.map((d) => (
                    <MenuItem key={d.id} value={d.id}>{d.name}</MenuItem>
                  ))}
                </Select>
              </FormControl>
              <TextField
                fullWidth
                label="–ö–∞–ª–æ—Ä–∏–∏"
                type="number"
                margin="normal"
                value={formData.calories || ""}
                onChange={(e) => setFormData({ ...formData, calories: parseInt(e.target.value) })}
              />
            </>
          )}
          {dialogType === "disease" && (
            <>
              <TextField
                fullWidth
                label="–ù–∞–∑–≤–∞–Ω–∏–µ"
                margin="normal"
                value={formData.name || ""}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              <TextField
                fullWidth
                label="–û–ø–∏—Å–∞–Ω–∏–µ"
                margin="normal"
                multiline
                rows={3}
                value={formData.description || ""}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              />
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose}>–û—Ç–º–µ–Ω–∞</Button>
          <Button onClick={handleFormSubmit} variant="contained">{editItem ? "–°–æ—Ö—Ä–∞–Ω–∏—Ç—å" : "–°–æ–∑–¥–∞—Ç—å"}</Button>
        </DialogActions>
      </Dialog>

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert severity={snackbar.severity} onClose={() => setSnackbar({ ...snackbar, open: false })} variant="filled">
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AdminPage;
